                throw new ArgumentsRequired($this->id . ' fetchTrades () requires an extra parameter $params["lastId"]');
            }
            $market = $this->market($symbol);
            $request = array(
                'market' => $market['id'],
                'lastId' => $lastId,
            );
            if ($limit !== null) {
                $request['limit'] = $limit;
            }
            $response = Async\await($this->publicGetHistory ($this->extend($request, $params)));
            //
            //    {
            //        success => true,
            //        errorCode => '',
