                $marketId = $keys[$i];
                $marketOrders = $result[$marketId];
                $parsedOrders = $this->parse_orders($marketOrders, $market, $since, $limit);
                $orders = $this->array_concat($orders, $parsedOrders);
            }
            return $orders;
        }) ();
    }

