    public function fetch_ohlcv(string $symbol, $timeframe = '1m', ?int $since = null, ?int $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $timeframe, $since, $limit, $params) {
            /**
             * fetches historical candlestick data containing the open, high, low, and close price, and the volume of a $market
             * @see https://docs.poloniex.com/#public-endpoints-$market-data-candles
             * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
             * @param {string} $timeframe the length of time each candle represents
             * @param {int|null} $since timestamp in ms of the earliest candle to fetch
             * @param {int|null} $limit the maximum amount of candles to fetch
             * @param {array} $params extra parameters specific to the poloniex api endpoint
             * @return {[[int]]} A list of candles ordered, open, high, low, close, volume
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
                'interval' => $this->safe_string($this->timeframes, $timeframe, $timeframe),
            );
            if ($since !== null) {
                $request['startTime'] = $since;
            }
            if ($limit !== null) {
                // $limit should in between 100 and 500
                $request['limit'] = $limit;
            }
            $response = Async\await($this->publicGetMarketsSymbolCandles (array_merge($request, $params)));
            //
            //     array(
            //         array(
            //             "22814.01",
            //             "22937.42",
            //             "22832.57",
            //             "22937.42",
            //             "3916.58764051",
            //             "0.171199",
            //             "2982.64647063",
            //             "0.130295",
            //             33,
            //             0,
            //             "22877.449915304470460711",
            //             "MINUTE_5",
            //             1659664800000,
            //             1659665099999
            //         )
            //     )
            //
            return $this->parse_ohlcvs($response, $market, $timeframe, $since, $limit);
        }) ();
    }

    public function load_markets($reload = false, $params = array ()) {
        return Async\async(function () use ($reload, $params) {
            $markets = Async\await(parent::load_markets($reload, $params));
            $currenciesByNumericId = $this->safe_value($this->options, 'currenciesByNumericId');
            if (($currenciesByNumericId === null) || $reload) {
                $this->options['currenciesByNumericId'] = $this->index_by($this->currencies, 'numericId');
            }
            return $markets;
        }) ();
    }

    public function fetch_markets($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * retrieves data on all $markets for poloniex
             * @see https://docs.poloniex.com/#public-endpoints-reference-data-symbol-information
             * @param {array} $params extra parameters specific to the exchange api endpoint
             * @return {[array]} an array of objects representing $market data
             */
            $markets = Async\await($this->publicGetMarkets ($params));
            //
            //     array(
            //         {
            //             "symbol" : "BTS_BTC",
            //             "baseCurrencyName" : "BTS",
            //             "quoteCurrencyName" : "BTC",
            //             "displayName" : "BTS/BTC",
            //             "state" : "NORMAL",
            //             "visibleStartTime" : 1659018816626,
            //             "tradableStartTime" : 1659018816626,
            //             "symbolTradeLimit" : {
            //                 "symbol" : "BTS_BTC",
            //                 "priceScale" : 10,
            //                 "quantityScale" : 0,
            //                 "amountScale" : 8,
            //                 "minQuantity" : "100",
            //                 "minAmount" : "0.00001",
            //                 "highestBid" : "0",
            //                 "lowestAsk" : "0"
            //             }
            //         }
            //     )
            //
            $result = array();
            for ($i = 0; $i < count($markets); $i++) {
                $market = $this->safe_value($markets, $i);
                $id = $this->safe_string($market, 'symbol');
                $baseId = $this->safe_string($market, 'baseCurrencyName');
                $quoteId = $this->safe_string($market, 'quoteCurrencyName');
                $base = $this->safe_currency_code($baseId);
                $quote = $this->safe_currency_code($quoteId);
                $state = $this->safe_string($market, 'state');
                $active = $state === 'NORMAL';
                $symbolTradeLimit = $this->safe_value($market, 'symbolTradeLimit');
                // these are known defaults
                $result[] = array(
                    'id' => $id,
                    'symbol' => $base . '/' . $quote,
                    'base' => $base,
                    'quote' => $quote,
                    'settle' => null,
                    'baseId' => $baseId,
                    'quoteId' => $quoteId,
                    'settleId' => null,
                    'type' => 'spot',
                    'spot' => true,
                    'margin' => false,
                    'swap' => false,
                    'future' => false,
                    'option' => false,
                    'active' => $active,
                    'contract' => false,
                    'linear' => null,
                    'inverse' => null,
                    'contractSize' => null,
                    'expiry' => null,
                    'expiryDatetime' => null,
                    'strike' => null,
                    'optionType' => null,
                    'precision' => array(
                        'amount' => $this->parse_number($this->parse_precision($this->safe_string($symbolTradeLimit, 'quantityScale'))),
                        'price' => $this->parse_number($this->parse_precision($this->safe_string($symbolTradeLimit, 'priceScale'))),
                    ),
                    'limits' => array(
                        'amount' => array(
                            'min' => $this->safe_number($symbolTradeLimit, 'minQuantity'),
                            'max' => null,
                        ),
                        'price' => array(
                            'min' => null,
                            'max' => null,
                        ),
                        'cost' => array(
                            'min' => $this->safe_number($symbolTradeLimit, 'minAmount'),
                            'max' => null,
                        ),
                    ),
                    'info' => $market,
                );
            }
            return $result;
        }) ();
    }

    public function fetch_time($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * fetches the current integer timestamp in milliseconds from the exchange server
             * @see https://docs.poloniex.com/#public-endpoints-reference-data-system-timestamp
             * @param {array} $params extra parameters specific to the poloniex api endpoint
             * @return {int} the current integer timestamp in milliseconds from the exchange server
             */
            $response = Async\await($this->publicGetTimestamp ($params));
            return $this->safe_integer($response, 'serverTime');
        }) ();
    }

    public function parse_ticker($ticker, $market = null) {
        //
        //     {
        //         "symbol" : "BTC_USDT",
        //         "open" : "22814.93",
        //         "low" : "22441.90",
        //         "high" : "23413.00",
        //         "close" : "23148.66",
        //         "quantity" : "71.743706",
        //         "amount" : "1638994.52683452",
        //         "tradeCount" : 3893,
        //         "startTime" : 1659605760000,
        //         "closeTime" : 1659692161077,
        //         "displayName" : "BTC/USDT",
        //         "dailyChange" : "0.0152",
        //         "ts" : 1659692169838
        //     }
        //
        $timestamp = $this->safe_integer($ticker, 'ts');
        $marketId = $this->safe_string($ticker, 'symbol');
        $market = $this->safe_market($marketId);
        $close = $this->safe_string($ticker, 'close');
        $relativeChange = $this->safe_string($ticker, 'percentChange');
        $percentage = Precise::string_mul($relativeChange, '100');
        return $this->safe_ticker(array(
            'id' => $marketId,
