                $data = $this->safe_dict($responseRaw, 'data', array());
                $ts = $this->safe_integer($data, 'ts');
                return $this->parse_order_book($data, $symbol, $ts);
            }
            $response = Async\await($this->publicGetMarketsSymbolOrderBook ($this->extend($request, $params)));
            //
            //     {
            //         "time" : 1659695219507,
