            //                 "market_id":"ETH-BTC",
            //                 "time":"2020-04-12T18:43:38.000Z"
            //             }
            //         )
            //     }
            //
            $data = $this->safe_value($response, 'data', array());
            return $this->parse_tickers($data, $symbols);
        }) ();
