            $base = $this->safe_currency_code($baseId);
            $quote = $this->safe_currency_code($quoteId);
            $symbol = $base . '/' . $quote;
            $precision = array(
                'amount' => $this->safe_number($market, 'min_amount'),
                'price' => $this->safe_number($market, 'price_tick'),
            );
            $limits = array(
                'amount' => array(
                    'min' => $this->safe_number($market, 'min_amount'),
