    public function parse_order_status($status) {
        $statuses = array(
            'OPEN' => 'open',
            'PART' => 'open',
            'CLOS' => 'canceled',
            'CANC' => 'canceled',
            'COMP' => 'closed',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_order($order, $market = null) {
        //
        // createOrder, cancelOrder, fetchOpenOrders, fetchClosedOrders, fetchOrders, fetchOrder
        //
        //     {
        //         "order_id" => "286e560e-b8a2-464b-8b84-15a7e2a67eab",
        //         "pair" => "BTC_ARS",
        //         "$side" => "SELL",
        //         "$amount" => "0.00100",
        //         "notional" => null,
        //         "fill_or_kill" => false,
        //         "all_or_none" => false,
        //         "order_type" => "LIMIT",
        //         "$status" => "CANC",
        //         "created_at" => 1575472707,
        //         "$filled" => "0.00000",
        //         "limit_price" => "681000.00",
        //         "stop_price" => null,
        //         "distance" => null
        //     }
        //
        //     {
        //         "order_id":"d6b60c01-8624-44f2-9e6c-9e8cd677ea5c",
        //         "pair":"BTC_USDC",
        //         "$side":"BUY",
        //         "$amount":"0.00200",
        //         "notional":"50",
        //         "fill_or_kill":false,
        //         "all_or_none":false,
        //         "order_type":"MARKET",
        //         "$status":"OPEN",
        //         "created_at":1601730306,
        //         "$filled":"0.00000",
        //         "fill_price":10593.99,
        //         "fee":0.0,
        //         "$fills":array(
        //             {
        //                 "pair":"BTC_USDC",
        //                 "exchanged":0.002,
        //                 "match_price":10593.99,
        //                 "maker_fee":0.0,
        //                 "taker_fee":0.0,
        //                 "$timestamp":1601730306942
        //             }
        //         ),
        //         "filled_at":"2020-10-03T13:05:06.942186Z",
        //         "limit_price":"0.000000",
        //         "stop_price":null,
        //         "distance":null
        //     }
        //
        $id = $this->safe_string($order, 'order_id');
        $amount = $this->safe_number($order, 'amount');
        $cost = $this->safe_number($order, 'notional');
        $type = $this->safe_string_lower($order, 'order_type');
        $priceField = ($type === 'market') ? 'fill_price' : 'limit_price';
        $price = $this->safe_number($order, $priceField);
        $side = $this->safe_string_lower($order, 'side');
        $status = $this->parse_order_status($this->safe_string($order, 'status'));
        $timestamp = $this->safe_timestamp($order, 'created_at');
        $average = $this->safe_value($order, 'fill_price');
        $filled = $this->safe_number($order, 'filled');
        $remaining = null;
        $fills = $this->safe_value($order, 'fills');
        $trades = null;
        $lastTradeTimestamp = null;
        if ($fills !== null) {
            $numFills = is_array($fills) ? count($fills) : 0;
            if ($numFills > 0) {
                $filled = 0;
                $cost = 0;
                $trades = $this->parse_trades($fills, $market, null, null, array(
                    'order' => $id,
                    'side' => $side,
                ));
                for ($i = 0; $i < count($trades); $i++) {
                    $trade = $trades[$i];
                    $filled = $this->sum($trade['amount'], $filled);
                    $cost = $this->sum($trade['cost'], $cost);
                    $lastTradeTimestamp = $trade['timestamp'];
                }
                if (($average === null) && ($filled > 0)) {
                    $average = $cost / $filled;
                }
            }
        }
        if ($filled !== null) {
            if (($cost === null) && ($price !== null)) {
                $cost = $price * $filled;
            }
            if ($amount !== null) {
                $remaining = max (0, $amount - $filled);
            }
        }
        $marketId = $this->safe_string($order, 'pair');
        $symbol = $this->safe_symbol($marketId, $market, '_');
        $stopPrice = $this->safe_number($order, 'stop_price');
        return array(
            'id' => $id,
            'clientOrderId' => null,
            'info' => $order,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => $lastTradeTimestamp,
            'symbol' => $symbol,
            'type' => $type,
            'timeInForce' => null,
            'postOnly' => null,
            'side' => $side,
            'price' => $price,
            'stopPrice' => $stopPrice,
            'amount' => $amount,
            'cost' => $cost,
            'average' => $average,
            'filled' => $filled,
            'remaining' => $remaining,
            'status' => $status,
            'fee' => null,
            'trades' => $trades,
        );
    }

