        $minAmount = Precise::string_max($amountIncrement, $minBase);
        $priceIncrement = $this->safe_string($market, 'tickSize');
        $minCost = $this->safe_string($market, 'quoteMinSize');
        return array(
            'id' => $id,
            'symbol' => $base . '/' . $quote,
            'base' => $base,
            'quote' => $quote,
            'settle' => null,
            'baseId' => $baseId,
