    public function fetch_markets(?array $symbols = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbols, $params) {
            /**
             * retrieves data on all markets for twox
             * @see https://api.twox.ir/api/currencies
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array[]} an array of objects representing $market data
             */
            $response = Async\await($this->publicGetApiCurrencies ($params));
            $result = array();
            $quotes = array( 'IRT', 'USDT' );
            for ($i = 0; $i < count($response); $i++) {
                $marketData = $this->extend(array(), $response[$i]);
                $base = $this->safe_string($marketData, 'symbol');
                for ($index = 0; $index < count($quotes); $index++) {
                    $quote = $quotes[$index];
                    if ($base === $quote || $marketData['sellPrice'] === 0 || $base === 'IRT') {
                        break;
                    }
                    $marketData['base'] = $base;
                    $marketData['quote'] = $quote;
                    $market = Async\await($this->parse_market($marketData));
                    $result[] = $market;
                }
            }
            return $result;
        }) ();
    }

