    public function fetch_tickers(?array $symbols = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbols, $params) {
            /**
             * @see https://docs.wazirx.com/#24hr-$tickers-price-change-statistics
             * fetches price $tickers for multiple markets, statistical information calculated over the past 24 hours for each market
             * @param {string[]|null} $symbols unified $symbols of the markets to fetch the $ticker for, all market $tickers are returned if not assigned
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a dictionary of ~@link https://docs.ccxt.com/#/?id=$ticker-structure $ticker structures~
             */
            Async\await($this->load_markets());
            $tickers = Async\await($this->publicGetTickers24hr ());
            //
            // array(
            //     {
            //        "symbol":"btcinr",
            //        "baseAsset":"btc",
            //        "quoteAsset":"inr",
            //        "openPrice":"3698486",
            //        "lowPrice":"3641155.0",
            //        "highPrice":"3767999.0",
            //        "lastPrice":"3713212.0",
            //        "volume":"254.11582",
            //        "bidPrice":"3715021.0",
            //        "askPrice":"3715022.0",
            //     }
            //     ...
            // )
            //
            $result = array();
            for ($i = 0; $i < count($tickers); $i++) {
                $ticker = $tickers[$i];
                $parsedTicker = $this->parse_ticker($ticker);
                $symbol = $parsedTicker['symbol'];
                $result[$symbol] = $parsedTicker;
            }
            return $this->filter_by_array_tickers($result, 'symbol', $symbols);
        }) ();
    }

    public function fetch_trades(string $symbol, ?int $since = null, ?int $limit = null, $params = array ()): PromiseInterface {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * @see https://docs.wazirx.com/#recent-trades-list
             * get the list of most recent trades for a particular $symbol
             * @param {string} $symbol unified $symbol of the $market to fetch trades for
             * @param {int} [$since] timestamp in ms of the earliest trade to fetch
             * @param {int} [$limit] the maximum amount of trades to fetch
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {Trade[]} a list of ~@link https://docs.ccxt.com/#/?id=public-trades trade structures~
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'symbol' => $market['id'],
            );
            if ($limit !== null) {
                $request['limit'] = min ($limit, 1000); // Default 500; max 1000.
            }
            $method = $this->safe_string($this->options, 'fetchTradesMethod', 'publicGetTrades');
            $response = null;
            if ($method === 'privateGetHistoricalTrades') {
                $response = Async\await($this->privateGetHistoricalTrades (array_merge($request, $params)));
            } else {
                $response = Async\await($this->publicGetTrades (array_merge($request, $params)));
            }
            // array(
            //     array(
            //         "id":322307791,
            //         "price":"93.7",
            //         "qty":"0.7",
            //         "quoteQty":"65.59",
            //         "time":1641386701000,
            //         "isBuyerMaker":false
            //     ),
            // )
            return $this->parse_trades($response, $market, $since, $limit);
        }) ();
    }

    public function parse_trade($trade, ?array $market = null): array {
        //
        //     {
        //         "id":322307791,
        //         "price":"93.7",
        //         "qty":"0.7",
        //         "quoteQty":"65.59",
        //         "time":1641386701000,
        //         "isBuyerMaker":false
        //     }
        //
        $id = $this->safe_string($trade, 'id');
        $timestamp = $this->safe_integer($trade, 'time');
        $datetime = $this->iso8601($timestamp);
        $market = $this->safe_market(null, $market);
        $isBuyerMaker = $this->safe_value($trade, 'isBuyerMaker');
        $side = $isBuyerMaker ? 'sell' : 'buy';
        $price = $this->safe_number($trade, 'price');
        $amount = $this->safe_number($trade, 'qty');
        $cost = $this->safe_number($trade, 'quoteQty');
        return $this->safe_trade(array(
            'info' => $trade,
            'id' => $id,
            'timestamp' => $timestamp,
            'datetime' => $datetime,
            'symbol' => $market['symbol'],
            'order' => $id,
            'type' => null,
            'side' => $side,
            'takerOrMaker' => null,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => null,
        ), $market);
    }

