            //             "networkList" => [
            //                 {
            //                     "addressRegex" => "^[13][a-km-zA-HJ-NP-Z1-9]array(25,34)$|^(bc1)[0-9A-Za-z]array(39,59)$",
            //                     "confirmations" => 4,
