    public function set_leverage(int $leverage, ?string $symbol = null, $params = array ()) {
        return Async\async(function () use ($leverage, $symbol, $params) {
            /**
             * set the level of $leverage for a $market
             * @see https://webseaex.github.io/zh/#futures-trading-position-set-$leverage
             * @param {float} $leverage the rate of $leverage
             * @param {string} $symbol unified $market $symbol
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} response from the exchange
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            if ($market['type'] !== 'swap') {
                throw new BadSymbol($this->id . ' setLeverage() supports swap contracts only');
            }
            $request = array(
                'symbol' => $market['id'],
                'leverage' => $leverage,
            );
            return Async\await($this->privatePostOpenApiFuturesPositionSetLeverage ($this->extend($request, $params)));
        }) ();
    }

    public function parse_order($order, ?array $market = null): array {
        //
        // futures => openApi/futures/entrust/orderDetail
        //     {
        //         "order_id" => "123456789",
        //         "symbol" => "BTC-USDT",
        //         "side" => "buy",
        //         "type" => "limit",
        //         "price" => "60000.00",
        //         "amount" => "1.000",
        //         "filled_amount" => "0.500",
        //         "unfilled_amount" => "0.500",
        //         "status" => "partially_filled",
        //         "create_time" => 1630000000000,
        //         "update_time" => 1630000001000
        //     }
        //
        $marketId = $this->safe_string($order, 'symbol');
        $market = $this->safe_market($marketId, $market);
        $symbol = $market['symbol'];
        $id = $this->safe_string($order, 'order_id');
        $timestamp = $this->safe_integer($order, 'create_time');
        $status = $this->safe_string($order, 'status');
        $side = $this->safe_string($order, 'side');
        $type = $this->safe_string($order, 'type');
        $price = $this->safe_number($order, 'price');
        $amount = $this->safe_number($order, 'amount');
        $filled = $this->safe_number($order, 'filled_amount');
        $remaining = $this->safe_number($order, 'unfilled_amount');
        $lastTradeTimestamp = $this->safe_integer($order, 'update_time');
        return $this->safe_order(array(
            'info' => $order,
            'id' => $id,
            'clientOrderId' => null,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => $lastTradeTimestamp,
            'symbol' => $symbol,
