    public function fetch_markets($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * retrieves data on all markets for websea
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array[]} an array of objects representing market data
             */
            // 获取支持的市场类型（基于has属性）
            $supportedTypes = array();
            if ($this->has['spot']) {
                $supportedTypes[] = 'spot';
            }
            if ($this->has['swap']) {
                $supportedTypes[] = 'swap';
            }
            if ($this->has['future']) {
                $supportedTypes[] = 'future';
            }
            if ($this->has['option']) {
                $supportedTypes[] = 'option';
            }
            // 获取用户指定的市场类型或使用默认值
            $fetchMarketsOptions = $this->safe_dict($this->options, 'fetchMarkets', array());
            $requestedTypes = $this->safe_list($fetchMarketsOptions, 'types', $supportedTypes);
            // 验证请求的市场类型是否支持
            $validTypes = array();
            for ($i = 0; $i < count($requestedTypes); $i++) {
                $type = $requestedTypes[$i];
                $typeFound = false;
                for ($j = 0; $j < count($supportedTypes); $j++) {
                    if ($supportedTypes[$j] === $type) {
                        $typeFound = true;
                        break;
                    }
                }
                if ($typeFound) {
                    $validTypes[] = $type;
                }
            }
            // 如果没有有效的市场类型，返回空数组
            if (strlen($validTypes) === 0) {
                return array();
            }
            // 并行获取市场数据
            $promises = array();
            for ($i = 0; $i < count($validTypes); $i++) {
                $type = $validTypes[$i];
                $promises[] = $this->fetch_markets_by_type($type, $params);
            }
            $results = Async\await(Promise\all($promises));
            // 使用循环合并所有市场数据
            $allMarkets = array();
            for ($i = 0; $i < count($results); $i++) {
                $allMarkets = $this->array_concat($allMarkets, $results[$i]);
            }
            return $allMarkets;
        }) ();
    }

    public function fetch_markets_by_type(string $type, $params = array ()): PromiseInterface {
        return Async\async(function () use ($type, $params) {
            // 首先获取货币列表，确保所有货币代码都存在
            $currenciesResponse = Async\await($this->publicGetOpenApiMarketCurrencies ($params));
            $currencies = $this->safe_value($currenciesResponse, 'result', array());
            $markets = array();
            if ($type === 'spot') {
                // 获取现货市场数据
                $spotResponse = Async\await($this->publicGetOpenApiMarketSymbols ($params));
                $markets = $this->safe_value($spotResponse, 'result', array());
            } elseif ($type === 'swap') {
                // 尝试获取合约市场数据
                try {
                    $swapResponse = Async\await($this->contractGetOpenApiContractSymbols ($params));
                    $markets = $this->safe_value($swapResponse, 'result', array());
                } catch (Exception $e) {
                    // 如果合约API不可用，返回空数组
                    // This is expected behavior if no swap $markets are available
                    return array();
                }
                // 为合约市场特有的货币代码创建虚拟的货币条目
                $swapCurrencies = array();
                for ($i = 0; $i < count($markets); $i++) {
                    $market = $markets[$i];
                    $baseId = $this->safe_string($market, 'base_currency');
                    $quoteId = $this->safe_string($market, 'quote_currency');
                    if ($baseId && !(is_array($currencies) && array_key_exists($baseId, $currencies))) {
                        $baseIdFound = false;
                        for ($k = 0; $k < count($swapCurrencies); $k++) {
                            if ($swapCurrencies[$k] === $baseId) {
                                $baseIdFound = true;
                                break;
                            }
                        }
                        if (!$baseIdFound) {
                            $swapCurrencies[] = $baseId;
                        }
                    }
                    if ($quoteId && !(is_array($currencies) && array_key_exists($quoteId, $currencies))) {
                        $quoteIdFound = false;
                        for ($k = 0; $k < count($swapCurrencies); $k++) {
                            if ($swapCurrencies[$k] === $quoteId) {
                                $quoteIdFound = true;
                                break;
                            }
                        }
                        if (!$quoteIdFound) {
                            $swapCurrencies[] = $quoteId;
                        }
                    }
                }
                // 将合约市场特有的货币代码添加到货币列表中
                for ($j = 0; $j < count($swapCurrencies); $j++) {
                    $currencyId = $swapCurrencies[$j];
                    $currencies[$currencyId] = array(
                        'name' => $currencyId,
                        'canWithdraw' => false,
                        'canDeposit' => false,
                        'minWithdraw' => '0',
                        'maxWithdraw' => '0',
                        'makerFee' => '0.0016',
                        'takerFee' => '0.0018',
                    );
                    // 同时添加到$this->currencies字典中，以便通过CCXT的货币代码验证
                    if ($this->currencies === null) {
                        $this->currencies = array();
                    }
                    $this->currencies[$currencyId] = array(
                        'id' => $currencyId,
                        'code' => $currencyId,
                        'name' => $currencyId,
                        'active' => false,
                        'deposit' => false,
                        'withdraw' => false,
                        'precision' => null,
                        'fee' => null,
                        'limits' => array(
                            'amount' => array( 'min' => null, 'max' => null ),
                            'withdraw' => array( 'min' => 0, 'max' => 0 ),
                        ),
                        'networks' => array(),
                        'info' => null,
                    );
                }
            }
            // 解析货币列表
            $this->parse_currencies($currencies);
            // 为市场添加$type字段
            for ($i = 0; $i < count($markets); $i++) {
                $markets[$i]['type'] = $type;
            }
            return $this->parse_markets($markets);
        }) ();
    }

    public function parse_market($market): array {
        //
        // 现货市场:
        //     {
        //         "id" => 1223,
        //         "symbol" => "BTC-USDT",
        //         "base_currency" => "BTC",
        //         "quote_currency" => "USDT",
        //         "min_size" => 0.0000001,
        //         "max_size" => 10000,
        //         "min_price" => 0.001,
        //         "max_price" => 1000,
        //         "maker_fee" => 0.002,
        //         "taker_fee" => 0.002
        //     }
        //
        // 合约市场:
        //     {
        //         "base_currency" => "BTC",
        //         "symbol" => "BTC-USDT",
        //         "max_price" => "150000",
        //         "min_price" => "1",
        //         "max_hold" => "350000",
        //         "maker_fee" => 1,
        //         "taker_fee" => 1,
        //         "min_size" => "1",
        //         "id" => 1,
        //         "contract_size" => "0.001",
        //         "quote_currency" => "USDT",
        //         "max_size" => "175000"
        //     }
        //
        $marketId = $this->safe_string($market, 'symbol');
        $baseId = $this->safe_string($market, 'base_currency');
        $quoteId = $this->safe_string($market, 'quote_currency');
        // 检测是否为永续合约市场
        $marketType = $this->safe_string($market, 'type', 'spot');
        $isSwap = $marketType === 'swap';
        // 使用更宽松的货币代码处理策略
        // 对于合约市场，允许使用原始货币ID，因为合约市场可能包含现货市场不存在的货币代码
        $base = null;
        $quote = null;
        if ($isSwap) {
            // 对于合约市场，直接使用原始ID，避免货币代码验证错误
            $base = $baseId;
            $quote = $quoteId;
        } else {
            // 对于现货市场，使用标准的货币代码验证
            $base = $this->safe_currency_code($baseId);
            $quote = $this->safe_currency_code($quoteId);
            // 如果货币代码不存在，使用原始ID作为备用方案
            if ($base === null) {
                $base = $baseId;
            }
            if ($quote === null) {
                $quote = $quoteId;
            }
        }
        $minAmount = $this->safe_number($market, 'min_size');
        $maxAmount = $this->safe_number($market, 'max_size');
        $minPrice = $this->safe_number($market, 'min_price');
        $maxPrice = $this->safe_number($market, 'max_price');
        $isSpot = $marketType === 'spot';
        // Convert $market ID to unified $symbol format
        // 对于swap市场，使用标准的CCXT格式 => BASE/QUOTE:QUOTE
        $symbol = $isSpot ? ($base . '/' . $quote) : ($base . '/' . $quote . ':' . $quote);
        // Calculate precision from min values - derive tick sizes from the minimum values
        $minSizeString = $this->safe_string($market, 'min_size');
        $minPriceString = $this->safe_string($market, 'min_price');
        // For TICK_SIZE mode, we need to ensure precision values are proper tick sizes
        // Use the minimum values sizes, but ensure they're not problematic integers
        $amountPrecision = $this->parse_number($minSizeString);
        $pricePrecision = $this->parse_number($minPriceString);
        // Ensure precision values are valid tick sizes (not integers like 5.0)
        // Convert problematic integer-like values to proper decimal tick sizes
        if ($amountPrecision !== null && $amountPrecision >= 1 && fmod($amountPrecision, 1) === 0) {
            $amountPrecision = $this->parse_number('0.00000001'); // Default to 8 decimal places
        }
        if ($pricePrecision !== null && $pricePrecision >= 1 && fmod($pricePrecision, 1) === 0) {
            $pricePrecision = $this->parse_number('0.0001'); // Default to 4 decimal places for price
        }
        // If precision values are still null, set safe defaults
        if ($amountPrecision === null) {
            $amountPrecision = $this->parse_number('0.00000001');
        }
        if ($pricePrecision === null) {
            $pricePrecision = $this->parse_number('0.0001');
        }
        return array(
            'id' => $marketId,
            'symbol' => $symbol,
            'base' => $base,
            'quote' => $quote,
            'settle' => $isSwap ? $quote : null, // 永续合约的结算币种
            'baseId' => $baseId,
            'quoteId' => $quoteId,
            'settleId' => $isSwap ? $quoteId : null,
            'type' => $isSwap ? 'swap' : 'spot',
            'spot' => $isSpot,
            'margin' => false,
            'swap' => $isSwap,
            'future' => false,
            'option' => false,
            'active' => true,
            'contract' => $isSwap,
            'linear' => $isSwap ? true : null, // U本位永续合约
            'inverse' => $isSwap ? false : null,
            'contractSize' => $this->safe_number($market, 'contract_size'), // 永续合约的合约大小
            'expiry' => null,
            'expiryDatetime' => null,
            'strike' => null,
            'optionType' => null,
            'precision' => array(
                'amount' => $amountPrecision,
                'price' => $pricePrecision,
            ),
            'limits' => array(
                'leverage' => array(
                    'min' => null,
                    'max' => null,
                ),
                'amount' => array(
                    'min' => $minAmount,
                    'max' => $maxAmount,
                ),
                'price' => array(
                    'min' => $minPrice,
                    'max' => $maxPrice,
                ),
                'cost' => array(
                    'min' => null,
                    'max' => null,
                ),
            ),
            'created' => null,
            'info' => $market,
        );
    }

