            //              ),
            //              "deposit":array(
            //                  "max_amount":"0",
            //                  "min_amount":"19.5",
            //                  "fixed":null,
            //                  "flex":null
            //               }
            //          ),
            //           array(...)
            //      }
            //
            $currenciesIds = is_array($response) ? array_keys($response) : array();
            $withdrawFees = array();
            $depositFees = array();
            for ($i = 0; $i < count($currenciesIds); $i++) {
                $currency = $currenciesIds[$i];
                $data = $response[$currency];
                $code = $this->safe_currency_code($currency);
                $withdraw = $this->safe_value($data, 'withdraw', array());
                $withdrawFees[$code] = $this->safe_string($withdraw, 'fixed');
                $deposit = $this->safe_value($data, 'deposit', array());
                $depositFees[$code] = $this->safe_string($deposit, 'fixed');
            }
            return array(
                'withdraw' => $withdrawFees,
                'deposit' => $depositFees,
                'info' => $response,
            );
        }) ();
    }

    public function fetch_trading_fees($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * fetch the trading fees for multiple markets
             * @param {array} $params extra parameters specific to the whitebit api endpoint
             * @return {array} a dictionary of {@link https://docs.ccxt.com/en/latest/manual.html#$fee-structure $fee structures} indexed by $market symbols
             */
            $response = Async\await($this->v4PublicGetAssets ($params));
            //
            //      {
            //          '1INCH' => array(
            //              name => '1inch',
            //              unified_cryptoasset_id => '8104',
            //              can_withdraw => true,
            //              can_deposit => true,
            //              min_withdraw => '33',
            //              max_withdraw => '0',
            //              maker_fee => '0.1',
            //              taker_fee => '0.1',
            //              min_deposit => '30',
            //              max_deposit => '0'
            //            ),
            //            ...
            //      }
            //
            $result = array();
            for ($i = 0; $i < count($this->symbols); $i++) {
                $symbol = $this->symbols[$i];
                $market = $this->market($symbol);
                $fee = $this->safe_value($response, $market['baseId'], array());
                $makerFee = $this->safe_string($fee, 'maker_fee');
                $takerFee = $this->safe_string($fee, 'taker_fee');
                $makerFee = Precise::string_div($makerFee, '100');
                $takerFee = Precise::string_div($takerFee, '100');
                $result[$symbol] = array(
                    'info' => $fee,
                    'symbol' => $market['symbol'],
                    'percentage' => true,
                    'tierBased' => false,
                    'maker' => $this->parse_number($makerFee),
                    'taker' => $this->parse_number($takerFee),
                );
            }
            return $result;
        }) ();
    }

    public function fetch_ticker($symbol, $params = array ()) {
        return Async\async(function () use ($symbol, $params) {
            /**
             * fetches a price $ticker, a statistical calculation with the information calculated over the past 24 hours for a specific $market
             * @param {string} $symbol unified $symbol of the $market to fetch the $ticker for
             * @param {array} $params extra parameters specific to the whitebit api endpoint
             * @return {array} a {@link https://docs.ccxt.com/en/latest/manual.html#$ticker-structure $ticker structure}
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'market' => $market['id'],
            );
            $response = Async\await($this->v1PublicGetTicker (array_merge($request, $params)));
            //
            //      {
            //         "success":true,
            //         "message":"",
            //         "result" => array(
            //             "bid":"0.021979",
            //             "ask":"0.021996",
            //             "open":"0.02182",
            //             "high":"0.022039",
            //             "low":"0.02161",
            //             "last":"0.021987",
            //             "volume":"2810.267",
            //             "deal":"61.383565474",
            //             "change":"0.76",
            //         ),
            //     }
            //
            $ticker = $this->safe_value($response, 'result', array());
            return $this->parse_ticker($ticker, $market);
        }) ();
    }

    public function parse_ticker($ticker, $market = null) {
        //
        //  FetchTicker (v1)
        //
        //    {
        //        "bid" => "0.021979",
        //        "ask" => "0.021996",
        //        "open" => "0.02182",
        //        "high" => "0.022039",
        //        "low" => "0.02161",
        //        "last" => "0.021987",
        //        "volume" => "2810.267",
        //        "deal" => "61.383565474",
        //        "change" => "0.76",
        //    }
        //
        // FetchTickers (v4)
        //
        //    "BCH_RUB" => {
        //        "base_id" => 1831,
        //        "quote_id" => 0,
        //        "last_price" => "32830.21",
        //        "quote_volume" => "1494659.8024096",
        //        "base_volume" => "46.1083",
        //        "isFrozen" => false,
        //        "change" => "2.12" // in percent
        //    }
        //
        $market = $this->safe_market(null, $market);
        $last = $this->safe_string($ticker, 'last_price');
        return $this->safe_ticker(array(
            'symbol' => $market['symbol'],
            'timestamp' => null,
            'datetime' => null,
            'high' => $this->safe_string($ticker, 'high'),
            'low' => $this->safe_string($ticker, 'low'),
            'bid' => $this->safe_string($ticker, 'bid'),
            'bidVolume' => null,
            'ask' => $this->safe_string($ticker, 'ask'),
            'askVolume' => null,
            'vwap' => null,
            'open' => $this->safe_string($ticker, 'open'),
            'close' => $last,
            'last' => $last,
            'previousClose' => null,
            'change' => null,
            'percentage' => $this->safe_string($ticker, 'change'),
            'average' => null,
            'baseVolume' => $this->safe_string_2($ticker, 'base_volume', 'volume'),
            'quoteVolume' => $this->safe_string_2($ticker, 'quote_volume', 'deal'),
            'info' => $ticker,
        ), $market);
    }

    public function fetch_tickers($symbols = null, $params = array ()) {
        return Async\async(function () use ($symbols, $params) {
            /**
             * fetches price tickers for multiple markets, statistical calculations with the information calculated over the past 24 hours each $market
             * @param {[string]|null} $symbols unified $symbols of the markets to fetch the $ticker for, all $market tickers are returned if not assigned
