    public function fetch_my_trades($symbol = null, $since = null, $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $since, $limit, $params) {
            /**
             * fetch all trades made by the user
             * @param {string} $symbol unified $symbol of the $market to fetch trades for
             * @param {int|null} $since timestamp in ms of the earliest trade to fetch
             * @param {int|null} $limit the maximum amount of trades to fetch
             * @param {array} $params extra parameters specific to the whitebit api endpoint
             * @return {[array]} a list of ~@link https://docs.ccxt.com/en/latest/manual.html?#public-trades trade structures~
             */
            Async\await($this->load_markets());
            $market = null;
            $request = array();
            if ($symbol !== null) {
                $market = $this->market($symbol);
                $request['market'] = $market['id'];
            }
            $response = Async\await($this->v4PrivatePostTradeAccountExecutedHistory (array_merge($request, $params)));
            //
            // when no $symbol is provided
            //
            //   {
            //       "USDC_USDT":array(
            //          array(
            //             "id":"1343815269",
            //             "clientOrderId":"",
            //             "time":"1641051917.532965",
            //             "side":"sell",
            //             "role":"2",
            //             "amount":"9.986",
            //             "price":"0.9995",
            //             "deal":"9.981007",
            //             "fee":"0.009981007",
            //             "orderId":"58166729555"
            //          ),
            //       )
            //   }
            //
            // when a $symbol is provided
            //
            //     array(
            //         array(
            //             'id' => 1343815269,
            //             'clientOrderId' => '',
            //             'time' => 1641051917.532965,
            //             'side' => 'sell',
            //             'role' => 2,
            //             'amount' => '9.986',
            //             'price' => '0.9995',
            //             'deal' => '9.981007',
            //             'fee' => '0.009981007',
            //             'orderId' => 58166729555,
            //         ),
            //     )
            //
            if (gettype($response) === 'array' && array_keys($response) === array_keys(array_keys($response))) {
                return $this->parse_trades($response, $market, $since, $limit);
            } else {
                $results = array();
                $keys = is_array($response) ? array_keys($response) : array();
                for ($i = 0; $i < count($keys); $i++) {
                    $marketId = $keys[$i];
                    $market = $this->safe_market($marketId, null, '_');
                    $rawTrades = $this->safe_value($response, $marketId, array());
                    $parsed = $this->parse_trades($rawTrades, $market, $since, $limit);
                    $results = $this->array_concat($results, $parsed);
                }
                $results = $this->sort_by_2($results, 'timestamp', 'id');
                $tail = ($since === null);
                return $this->filter_by_since_limit($results, $since, $limit, 'timestamp', $tail);
            }
        }) ();
    }

    public function parse_trade($trade, $market = null) {
        //
        // fetchTradesV4
        //
        //     {
        //       "tradeID" => 158056419,
        //       "price" => "9186.13",
        //       "quote_volume" => "0.0021",
        //       "base_volume" => "9186.13",
        //       "trade_timestamp" => 1594391747,
        //       "type" => "sell"
        //     }
        //
        // orderTrades (v4Private)
        //
        //     {
        //         "time" => 1593342324.613711,
        //         "fee" => "0.00000419198",
        //         "price" => "0.00000701",
        //         "amount" => "598",
        //         "id" => 149156519, // $trade $id
        //         "dealOrderId" => 3134995325, // $orderId
        //         "clientOrderId" => "customId11",
        //         "role" => 2, // 1 = maker, 2 = taker
        //         "deal" => "0.00419198" // $amount in money
        //     }
        //
        // fetchMyTrades
        //
        //      {
        //          'id' => 1343815269,
        //          'clientOrderId' => '',
        //          'time' => 1641051917.532965,
        //          'side' => 'sell',
        //          'role' => 2,
        //          'amount' => '9.986',
        //          'price' => '0.9995',
        //          'deal' => '9.981007',
        //          'fee' => '0.009981007',
        //          'orderId' => 58166729555,
        //      }
        //
        $market = $this->safe_market(null, $market);
        $timestamp = $this->safe_timestamp_2($trade, 'time', 'trade_timestamp');
        $orderId = $this->safe_string_2($trade, 'dealOrderId', 'orderId');
        $cost = $this->safe_string($trade, 'deal');
        $price = $this->safe_string($trade, 'price');
        $amount = $this->safe_string_2($trade, 'amount', 'base_volume');
        $id = $this->safe_string_2($trade, 'id', 'tradeID');
        $side = $this->safe_string_2($trade, 'type', 'side');
        $symbol = $market['symbol'];
        $role = $this->safe_integer($trade, 'role');
        $takerOrMaker = null;
        if ($role !== null) {
            $takerOrMaker = ($role === 1) ? 'maker' : 'taker';
        }
        $fee = null;
        $feeCost = $this->safe_string($trade, 'fee');
        if ($feeCost !== null) {
            $fee = array(
                'cost' => $feeCost,
                'currency' => $market['quote'],
            );
        }
        return $this->safe_trade(array(
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'id' => $id,
            'order' => $orderId,
            'type' => null,
            'takerOrMaker' => $takerOrMaker,
            'side' => $side,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => $fee,
        ), $market);
    }

    public function fetch_ohlcv($symbol, $timeframe = '1m', $since = null, $limit = null, $params = array ()) {
        return Async\async(function () use ($symbol, $timeframe, $since, $limit, $params) {
            /**
             * fetches historical candlestick data containing the open, high, low, and close price, and the volume of a $market
             * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
             * @param {string} $timeframe the length of time each candle represents
             * @param {int|null} $since timestamp in ms of the earliest candle to fetch
             * @param {int|null} $limit the maximum amount of candles to fetch
             * @param {array} $params extra parameters specific to the whitebit api endpoint
             * @return {[[int]]} A list of candles ordered as timestamp, open, high, low, close, volume
             */
            Async\await($this->load_markets());
            $market = $this->market($symbol);
            $request = array(
                'market' => $market['id'],
                'interval' => $this->timeframes[$timeframe],
            );
            if ($since !== null) {
                $maxLimit = 1440;
                if ($limit === null) {
                    $limit = $maxLimit;
                }
                $limit = min ($limit, $maxLimit);
                $start = intval($since / 1000);
                $duration = $this->parse_timeframe($timeframe);
                $end = $this->sum($start, $duration * $limit);
                $request['start'] = $start;
                $request['end'] = $end;
            }
            if ($limit !== null) {
                $request['limit'] = $limit; // max 1440
            }
            $response = Async\await($this->v1PublicGetKline (array_merge($request, $params)));
            //
            //     {
            //         "success":true,
            //         "message":"",
            //         "result":[
            //             [1591488000,"0.025025","0.025025","0.025029","0.025023","6.181","0.154686629"],
            //             [1591488060,"0.025028","0.025033","0.025035","0.025026","8.067","0.201921167"],
            //             [1591488120,"0.025034","0.02505","0.02505","0.025034","20.089","0.503114696"],
            //         ]
            //     }
            //
            $result = $this->safe_value($response, 'result', array());
            return $this->parse_ohlcvs($result, $market, $timeframe, $since, $limit);
        }) ();
    }

