    public function parse_position(array $position, ?array $market = null) {
        //
        // v1PrivateGetPositionSymbol
        //     {
        //         "symbol" => "PERP_ETH_USDT",
        //         "position_side" => "BOTH",
        //         "leverage" => 10,
        //         "margin_mode" => "CROSS",
