    public function fetch_status($params = array ()) {
        return Async\async(function () use ($params) {
            /**
             * the latest known information on the availability of the exchange API
             *
             * @see https://orderly.network/docs/build-on-evm/evm-api/restful-api/public/get-system-maintenance-$status
             *
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {array} a ~@link https://docs.ccxt.com/#/?id=exchange-$status-structure $status structure~
             */
            $response = Async\await($this->v1PublicGetPublicSystemInfo ($params));
            //
            //     {
            //         "success" => true,
            //         "data" => array(
            //             "status" => 0,
            //             "msg" => "System is functioning properly."
            //         ),
            //         "timestamp" => "1709274106602"
            //     }
            //
            $data = $this->safe_dict($response, 'data', array());
            $status = $this->safe_string($data, 'status');
            if ($status === null) {
                $status = 'error';
            } elseif ($status === '0') {
                $status = 'ok';
            } else {
                $status = 'maintenance';
            }
            return array(
                'status' => $status,
                'updated' => null,
                'eta' => null,
                'url' => null,
                'info' => $response,
            );
        }) ();
    }

    public function fetch_time($params = array ()): PromiseInterface {
        return Async\async(function () use ($params) {
            /**
             * fetches the current integer timestamp in milliseconds from the exchange server
             *
             * @see https://orderly.network/docs/build-on-evm/evm-api/restful-api/public/get-system-maintenance-status
             *
             * @param {array} [$params] extra parameters specific to the exchange API endpoint
             * @return {int} the current integer timestamp in milliseconds from the exchange server
             */
            $response = Async\await($this->v1PublicGetPublicSystemInfo ($params));
            //
            //     {
            //         "success" => true,
            //         "data" => array(
            //             "status" => 0,
            //             "msg" => "System is functioning properly."
            //         ),
            //         "timestamp" => "1709274106602"
            //     }
            //
            return $this->safe_integer($response, 'timestamp');
        }) ();
    }

    public function parse_market(array $market): array {
        //
        //   {
        //     "symbol" => "PERP_BTC_USDC",
        //     "quote_min" => 123,
        //     "quote_max" => 100000,
        //     "quote_tick" => 0.1,
        //     "base_min" => 0.00001,
        //     "base_max" => 20,
        //     "base_tick" => 0.00001,
        //     "min_notional" => 1,
        //     "price_range" => 0.02,
        //     "price_scope" => 0.4,
        //     "std_liquidation_fee" => 0.03,
        //     "liquidator_fee" => 0.015,
        //     "claim_insurance_fund_discount" => 0.0075,
        //     "funding_period" => 8,
        //     "cap_funding" => 0.000375,
        //     "floor_funding" => -0.000375,
        //     "interest_rate" => 0.0001,
        //     "created_time" => 1684140107326,
        //     "updated_time" => 1685345968053,
        //     "base_mmr" => 0.05,
        //     "base_imr" => 0.1,
        //     "imr_factor" => 0.0002512,
        //     "liquidation_tier" => "1"
        //   }
        //
        $marketId = $this->safe_string($market, 'symbol');
        $parts = explode('_', $marketId);
        $marketType = 'swap';
        $baseId = $this->safe_string($parts, 1);
        $quoteId = $this->safe_string($parts, 2);
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        $settleId = $this->safe_string($parts, 2);
        $settle = $this->safe_currency_code($settleId);
        $symbol = $base . '/' . $quote . ':' . $settle;
        return array(
            'id' => $marketId,
            'symbol' => $symbol,
            'base' => $base,
            'quote' => $quote,
            'settle' => $settle,
            'baseId' => $baseId,
            'quoteId' => $quoteId,
            'settleId' => $settleId,
            'type' => $marketType,
            'spot' => false,
            'margin' => false,
            'swap' => true,
            'future' => false,
            'option' => false,
            'active' => null,
            'contract' => true,
            'linear' => true,
            'inverse' => false,
            'contractSize' => $this->parse_number('1'),
            'expiry' => null,
            'expiryDatetime' => null,
            'strike' => null,
            'optionType' => null,
            'precision' => array(
                'amount' => $this->safe_number($market, 'base_tick'),
                'price' => $this->safe_number($market, 'quote_tick'),
            ),
            'limits' => array(
                'leverage' => array(
                    'min' => null,
                    'max' => null,
                ),
                'amount' => array(
                    'min' => $this->safe_number($market, 'base_min'),
                    'max' => $this->safe_number($market, 'base_max'),
                ),
                'price' => array(
                    'min' => $this->safe_number($market, 'quote_min'),
