            $isConditional = $triggerPrice !== null || $stopLoss !== null || $takeProfit !== null || ($this->safe_value($params, 'childOrders') !== null);
            $response = null;
            if ($isConditional) {
                $response = Async\await($this->v1PrivatePostAlgoOrder ($request));
                //
