                //       "client_order_id" => "testclientid",
                //       "algo_type" => "STOP",
                //       "quantity" => 100.12
                //     }
                // }
                //
            } else {
                $response = Async\await($this->v1PrivatePostOrder ($request));
                //
                // {
                //     "success" => true,
                //     "timestamp" => 1702989203989,
                //     "data" => {
                //       "order_id" => 13,
                //       "client_order_id" => "testclientid",
                //       "order_type" => "LIMIT",
                //       "order_price" => 100.12,
                //       "order_quantity" => 0.987654,
                //       "order_amount" => 0.8,
                //       "error_message" => "none"
                //     }
                // }
                //
            }
            $data = $this->safe_dict($response, 'data');
