                $depositsEnabled = ($depositStatus === 'enabled');
                $withdrawalsEnabled = ($withdrawalStatus === 'enabled');
                $active = $depositsEnabled && $withdrawalsEnabled;
                $result[$code] = array(
                    'id' => $id,
                    'numericId' => $numericId,
                    'code' => $code,
                    'name' => $this->safe_string($currency, 'name'),
                    'info' => $currency, // the original payload
