        $this->load_markets();
        $code = $this->safe_string($params, 'code');
        $params = $this->omit($params, 'code');
        $request = array();
        if ($code !== null) {
            $currency = $this->currency($code);
            $request['asset'] = $currency['id'];
        }
        $response = $this->v4PrivateGetUserdataAccounts (array_merge($request, $params));
        //
