            $taker = $feeTiers['taker'][$feeTier][1];
            for ($i = 0; $i < count($symbols); $i++) {
                $symbol = $symbols[$i];
                $result[$symbol] = array(
                    'info' => array(
                        'feeTier' => $feeTier,
                    ),
                    'symbol' => $symbol,
                    'maker' => $maker,
                    'taker' => $taker,
                );
            }
            return $result;
        } else if ($type === 'delivery') {
            //
            //     {
            //         "canDeposit" => true,
            //         "canTrade" => true,
            //         "canWithdraw" => true,
            //         "$feeTier" => 2,
            //         "updateTime" => 0
            //     }
            //
            $symbols = is_array($this->markets) ? array_keys($this->markets) : array();
            $result = array();
            $feeTier = $this->safe_integer($response, 'feeTier');
            $feeTiers = $this->fees[$type]['trading']['tiers'];
            $maker = $feeTiers['maker'][$feeTier][1];
            $taker = $feeTiers['taker'][$feeTier][1];
            for ($i = 0; $i < count($symbols); $i++) {
                $symbol = $symbols[$i];
                $result[$symbol] = array(
                    'info' => array(
                        'feeTier' => $feeTier,
                    ),
                    'symbol' => $symbol,
                    'maker' => $maker,
                    'taker' => $taker,
                );
            }
            return $result;
        }
    }

    public function futures_transfer($code, $amount, $type, $params = array ()) {
