        $marginType = $this->safe_string($position, 'marginType');
        if ($marginType === 'cross') {
            $liquidationPrice = null;
        }
        return array(
            'info' => $position,
            'symbol' => $symbol,
            'contracts' => $contracts,
            'unrealizedPnl' => $unrealizedPnl,
            'leverage' => $leverage,
