            $response = $this->sapiGetMarginAccount ($this->extend($request, $query));
        } elseif ($type === 'savings') {
            $response = $this->sapiGetLendingUnionAccount ($this->extend($request, $query));
        } elseif ($type === 'funding') {
            $response = $this->sapiPostAssetGetFundingAsset ($this->extend($request, $query));
