        //         array(
        //             "orderId" => 165123080,
        //             "symbol" => "BTCUSD_200925",
        //             "pair" => "BTCUSD",
        //             "status" => "FILLED",
        //             "clientOrderId" => "autoclose-1596542005017000006",
