            //         "startTime" => 1623824139000,
            //         "endTime" => 1626416139000,
            //         "limit" => 100,
            //         "moreData" => false
            //     }
            //
        }
        $rows = $this->safe_list($response, $responseQuery, array());
        return $this->parse_conversions($rows, $code, $fromCurrencyKey, $toCurrencyKey, $since, $limit);
    }

    public function parse_conversion(array $conversion, ?array $fromCurrency = null, ?array $toCurrency = null): array {
        //
        // fetchConvertQuote
        //
        //     {
        //         "quoteId":"12415572564",
        //         "ratio":"38163.7",
        //         "inverseRatio":"0.0000262",
        //         "validTimestamp":1623319461670,
        //         "toAmount":"3816.37",
        //         "fromAmount":"0.1"
        //     }
        //
        // createConvertTrade
        //
        //     {
        //         "orderId":"933256278426274426",
        //         "createTime":1623381330472,
        //         "orderStatus":"PROCESS"
        //     }
        //
        // createConvertTrade BUSD
        //
        //     {
        //         "tranId" => 118263407119,
        //         "status" => "S"
        //     }
        //
        // fetchConvertTrade, fetchConvertTradeHistory BUSD
        //
        //     {
        //         "tranId" => 118263615991,
        //         "type" => 244,
        //         "time" => 1664442078000,
        //         "deductedAsset" => "BUSD",
        //         "deductedAmount" => "1",
        //         "targetAsset" => "USDC",
        //         "targetAmount" => "1",
        //         "status" => "S",
        //         "accountType" => "MAIN"
        //     }
        //
        // fetchConvertTrade
        //
        //     {
        //         "orderId":933256278426274426,
        //         "orderStatus":"SUCCESS",
        //         "fromAsset":"BTC",
        //         "fromAmount":"0.00054414",
        //         "toAsset":"USDT",
        //         "toAmount":"20",
        //         "ratio":"36755",
        //         "inverseRatio":"0.00002721",
        //         "createTime":1623381330472
        //     }
        //
        // fetchConvertTradeHistory
        //
        //     {
        //         "quoteId" => "f3b91c525b2644c7bc1e1cd31b6e1aa6",
        //         "orderId" => 940708407462087195,
        //         "orderStatus" => "SUCCESS",
        //         "fromAsset" => "USDT",
        //         "fromAmount" => "20",
        //         "toAsset" => "BNB",
        //         "toAmount" => "0.06154036",
        //         "ratio" => "0.00307702",
        //         "inverseRatio" => "324.99",
        //         "createTime" => 1624248872184
        //     }
        //
        $timestamp = $this->safe_integer_n($conversion, array( 'time', 'validTimestamp', 'createTime' ));
        $fromCur = $this->safe_string_2($conversion, 'deductedAsset', 'fromAsset');
        $fromCode = $this->safe_currency_code($fromCur, $fromCurrency);
        $to = $this->safe_string_2($conversion, 'targetAsset', 'toAsset');
        $toCode = $this->safe_currency_code($to, $toCurrency);
        return array(
            'info' => $conversion,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'id' => $this->safe_string_n($conversion, array( 'tranId', 'orderId', 'quoteId' )),
            'fromCurrency' => $fromCode,
            'fromAmount' => $this->safe_number_2($conversion, 'deductedAmount', 'fromAmount'),
            'toCurrency' => $toCode,
            'toAmount' => $this->safe_number_2($conversion, 'targetAmount', 'toAmount'),
            'price' => null,
            'fee' => null,
        );
    }

    public function fetch_funding_intervals(?array $symbols = null, $params = array ()): array {
        /**
         * fetch the funding rate interval for multiple markets
         *
         * @see https://developers.binance.com/docs/derivatives/usds-margined-futures/market-data/rest-api/Get-Funding-Rate-Info
         * @see https://developers.binance.com/docs/derivatives/coin-margined-futures/market-data/rest-api/Get-Funding-Info
         *
         * @param {string[]} [$symbols] list of unified $market $symbols
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @param {string} [$params->subType] "linear" or "inverse"
         * @return {array[]} a list of ~@link https://docs.ccxt.com/#/?id=funding-rate-structure funding rate structures~
         */
        $this->load_markets();
        $market = null;
        if ($symbols !== null) {
            $symbols = $this->market_symbols($symbols);
            $market = $this->market($symbols[0]);
        }
        $type = 'swap';
        $subType = null;
        list($subType, $params) = $this->handle_sub_type_and_params('fetchFundingIntervals', $market, $params, 'linear');
        $response = null;
        if ($this->is_linear($type, $subType)) {
            $response = $this->fapiPublicGetFundingInfo ($params);
        } elseif ($this->is_inverse($type, $subType)) {
            $response = $this->dapiPublicGetFundingInfo ($params);
        } else {
            throw new NotSupported($this->id . ' fetchFundingIntervals() supports linear and inverse swap contracts only');
        }
        //
        //     array(
        //         array(
        //             "symbol" => "BLZUSDT",
        //             "adjustedFundingRateCap" => "0.03000000",
        //             "adjustedFundingRateFloor" => "-0.03000000",
        //             "fundingIntervalHours" => 4,
        //             "disclaimer" => false
        //         ),
        //     )
        //
        return $this->parse_funding_rates($response, $symbols);
    }

    public function fetch_long_short_ratio_history(?string $symbol = null, ?string $timeframe = null, ?int $since = null, ?int $limit = null, $params = array ()): array {
        /**
         * fetches the long short ratio history for a unified $market $symbol
         *
         * @see https://developers.binance.com/docs/derivatives/usds-margined-futures/market-data/rest-api/Long-Short-Ratio
         * @see https://developers.binance.com/docs/derivatives/coin-margined-futures/market-data/rest-api/Long-Short-Ratio
         *
         * @param {string} $symbol unified $symbol of the $market to fetch the long short ratio for
         * @param {string} [$timeframe] the period for the ratio, default is 24 hours
         * @param {int} [$since] the earliest time in ms to fetch ratios for
         * @param {int} [$limit] the maximum number of long short ratio structures to retrieve
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @param {int} [$params->until] timestamp in ms of the latest ratio to fetch
         * @return {array[]} an array of ~@link https://docs.ccxt.com/#/?id=long-short-ratio-structure long short ratio structures~
         */
        $this->load_markets();
        $market = $this->market($symbol);
        if ($timeframe === null) {
            $timeframe = '1d';
        }
        $request = array(
            'period' => $timeframe,
        );
        list($request, $params) = $this->handle_until_option('endTime', $request, $params);
        if ($since !== null) {
            $request['startTime'] = $since;
        }
        if ($limit !== null) {
            $request['limit'] = $limit;
        }
        $subType = null;
        list($subType, $params) = $this->handle_sub_type_and_params('fetchLongShortRatioHistory', $market, $params);
        $response = null;
        if ($subType === 'linear') {
            $request['symbol'] = $market['id'];
            $response = $this->fapiDataGetGlobalLongShortAccountRatio ($this->extend($request, $params));
            //
            //     array(
            //         array(
            //             "symbol" => "BTCUSDT",
            //             "longAccount" => "0.4558",
            //             "longShortRatio" => "0.8376",
            //             "shortAccount" => "0.5442",
            //             "timestamp" => 1726790400000
            //         ),
            //     )
            //
        } elseif ($subType === 'inverse') {
            $request['pair'] = $market['info']['pair'];
            $response = $this->dapiDataGetGlobalLongShortAccountRatio ($this->extend($request, $params));
            //
            //     array(
            //         array(
            //             "longAccount" => "0.7262",
            //             "longShortRatio" => "2.6523",
            //             "shortAccount" => "0.2738",
            //             "pair" => "BTCUSD",
            //             "timestamp" => 1726790400000
            //         ),
            //     )
            //
        } else {
            throw new BadRequest($this->id . ' fetchLongShortRatioHistory() supports linear and inverse subTypes only');
        }
        return $this->parse_long_short_ratio_history($response, $market);
    }

    public function parse_long_short_ratio(array $info, ?array $market = null): array {
        //
        // linear
        //
        //     {
        //         "symbol" => "BTCUSDT",
        //         "longAccount" => "0.4558",
        //         "longShortRatio" => "0.8376",
        //         "shortAccount" => "0.5442",
        //         "timestamp" => 1726790400000
        //     }
        //
        // inverse
        //
        //     {
        //         "longAccount" => "0.7262",
        //         "longShortRatio" => "2.6523",
        //         "shortAccount" => "0.2738",
        //         "pair" => "BTCUSD",
        //         "timestamp" => 1726790400000
        //     }
        //
        $marketId = $this->safe_string($info, 'symbol');
        $timestamp = $this->safe_integer_omit_zero($info, 'timestamp');
        return array(
            'info' => $info,
            'symbol' => $this->safe_symbol($marketId, $market, null, 'contract'),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'timeframe' => null,
            'longShortRatio' => $this->safe_number($info, 'longShortRatio'),
        );
    }
