    public function is_fiat($code) {
        return (is_array($this->options['fiat']) && array_key_exists($code, $this->options['fiat']));
    }

    public function get_currency_id($code) {
        return 'f' . $code;
    }

    public function get_currency_name($code) {
        // temporary fix for transpiler recognition, even though this is in parent class
        if (is_array($this->options['currencyNames']) && array_key_exists($code, $this->options['currencyNames'])) {
            return $this->options['currencyNames'][$code];
        }
        throw new NotSupported($this->id . ' ' . $code . ' not supported for withdrawal');
    }

    public function amount_to_precision($symbol, $amount) {
        // https://docs.bitfinex.com/docs/introduction#$amount-precision
        // The $amount field allows up to 8 decimals.
        // Anything exceeding this will be rounded to the 8th decimal.
        $symbol = $this->safe_symbol($symbol);
        return $this->decimal_to_precision($amount, TRUNCATE, $this->markets[$symbol]['precision']['amount'], DECIMAL_PLACES);
    }

    public function price_to_precision($symbol, $price) {
        $symbol = $this->safe_symbol($symbol);
        $price = $this->decimal_to_precision($price, ROUND, $this->markets[$symbol]['precision']['price'], $this->precisionMode);
        // https://docs.bitfinex.com/docs/introduction#$price-precision
        // The precision level of all trading prices is based on significant figures.
        // All pairs on Bitfinex use up to 5 significant digits and up to 8 decimals (e.g. 1.2345, 123.45, 1234.5, 0.00012345).
        // Prices submit with a precision larger than 5 will be cut by the API.
        return $this->decimal_to_precision($price, TRUNCATE, 8, DECIMAL_PLACES);
    }

    public function fetch_status($params = array ()) {
        /**
         * the latest known information on the availability of the exchange API
         * @see https://docs.bitfinex.com/reference/rest-public-platform-status
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} a ~@link https://docs.ccxt.com/#/?id=exchange-status-structure status structure~
         */
        //
        //    [1] // operative
        //    [0] // maintenance
        //
        $response = $this->publicGetPlatformStatus ($params);
        $statusRaw = $this->safe_string($response, 0);
        return array(
            'status' => $this->safe_string(array( '0' => 'maintenance', '1' => 'ok' ), $statusRaw, $statusRaw),
            'updated' => null,
            'eta' => null,
            'url' => null,
            'info' => $response,
        );
    }

    public function fetch_markets($params = array ()): array {
        /**
         * retrieves data on all $markets for bitfinex2
         * @see https://docs.bitfinex.com/reference/rest-public-conf
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array[]} an array of objects representing $market data
         */
        $spotMarketsInfo = $this->publicGetConfPubInfoPair ($params);
        $futuresMarketsInfo = $this->publicGetConfPubInfoPairFutures ($params);
        $spotMarketsInfo = $this->safe_value($spotMarketsInfo, 0, array());
        $futuresMarketsInfo = $this->safe_value($futuresMarketsInfo, 0, array());
        $markets = $this->array_concat($spotMarketsInfo, $futuresMarketsInfo);
        $marginIds = $this->publicGetConfPubListPairMargin ($params);
        $marginIds = $this->safe_value($marginIds, 0, array());
        //
        //    array(
        //        "1INCH:USD",
        //        array(
        //           null,
        //           null,
        //           null,
        //           "2.0",
        //           "100000.0",
        //           null,
        //           null,
        //           null,
        //           null,
        //           null,
        //           null,
        //           null
        //        )
        //    )
        //
        $result = array();
        for ($i = 0; $i < count($markets); $i++) {
            $pair = $markets[$i];
            $id = $this->safe_string_upper($pair, 0);
            $market = $this->safe_value($pair, 1, array());
            $spot = true;
            if (mb_strpos($id, 'F0') !== false) {
                $spot = false;
            }
            $swap = !$spot;
            $baseId = null;
            $quoteId = null;
            if (mb_strpos($id, ':') !== false) {
                $parts = explode(':', $id);
                $baseId = $parts[0];
                $quoteId = $parts[1];
            } else {
                $baseId = mb_substr($id, 0, 3 - 0);
                $quoteId = mb_substr($id, 3, 6 - 3);
            }
            $base = $this->safe_currency_code($baseId);
            $quote = $this->safe_currency_code($quoteId);
            $splitBase = explode('F0', $base);
            $splitQuote = explode('F0', $quote);
            $base = $this->safe_string($splitBase, 0);
            $quote = $this->safe_string($splitQuote, 0);
            $symbol = $base . '/' . $quote;
            $baseId = $this->get_currency_id($baseId);
            $quoteId = $this->get_currency_id($quoteId);
            $settle = null;
            $settleId = null;
            if ($swap) {
                $settle = $quote;
                $settleId = $quote;
                $symbol = $symbol . ':' . $settle;
            }
            $minOrderSizeString = $this->safe_string($market, 3);
            $maxOrderSizeString = $this->safe_string($market, 4);
            $margin = false;
            if ($spot && $this->in_array($id, $marginIds)) {
                $margin = true;
            }
            $result[] = array(
                'id' => 't' . $id,
                'symbol' => $symbol,
                'base' => $base,
                'quote' => $quote,
                'settle' => $settle,
                'baseId' => $baseId,
                'quoteId' => $quoteId,
                'settleId' => $settleId,
                'type' => $spot ? 'spot' : 'swap',
                'spot' => $spot,
                'margin' => $margin,
                'swap' => $swap,
                'future' => false,
                'option' => false,
                'active' => true,
                'contract' => $swap,
                'linear' => $swap ? true : null,
                'inverse' => $swap ? false : null,
                'contractSize' => $swap ? $this->parse_number('1') : null,
                'expiry' => null,
                'expiryDatetime' => null,
                'strike' => null,
                'optionType' => null,
                'precision' => array(
                    'amount' => intval('8'), // https://github.com/ccxt/ccxt/issues/7310
                    'price' => intval('5'),
                ),
                'limits' => array(
                    'leverage' => array(
                        'min' => null,
                        'max' => null,
                    ),
                    'amount' => array(
                        'min' => $this->parse_number($minOrderSizeString),
                        'max' => $this->parse_number($maxOrderSizeString),
                    ),
                    'price' => array(
                        'min' => $this->parse_number('1e-8'),
                        'max' => null,
                    ),
                    'cost' => array(
                        'min' => null,
                        'max' => null,
                    ),
                ),
                'created' => null, // todo => the api needs revision for extra $params & endpoints for possibility of returning a timestamp for this
                'info' => $market,
            );
        }
        return $result;
    }

    public function fetch_currencies($params = array ()): ?array {
        /**
         * fetches all available currencies on an exchange
         * @see https://docs.bitfinex.com/reference/rest-public-conf
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} an associative dictionary of currencies
         */
        $labels = array(
            'pub:list:currency',
            'pub:map:currency:sym', // maps symbols to their API symbols, BAB > BCH
            'pub:map:currency:label', // verbose friendly names, BNT > Bancor
            'pub:map:currency:unit', // maps symbols to unit of measure where applicable
            'pub:map:currency:undl', // maps derivatives symbols to their underlying currency
            'pub:map:currency:pool', // maps symbols to underlying network/protocol they operate on
            'pub:map:currency:explorer', // maps symbols to their recognised block explorer URLs
            'pub:map:currency:tx:fee', // maps currencies to their withdrawal $fees https://github.com/ccxt/ccxt/issues/7745,
            'pub:map:tx:method', // maps withdrawal/deposit methods to their API symbols
        );
        $config = implode(',', $labels);
        $request = array(
            'config' => $config,
        );
        $response = $this->publicGetConfConfig ($this->extend($request, $params));
        //
        //     [
        //
        //         a list of symbols
        //         ["AAA","ABS","ADA"],
        //
        //         // sym
        //         // maps symbols to their API symbols, BAB > BCH
        //         array(
        //             array( "BAB", "BCH" ),
        //             array( "CNHT", "CNHt" ),
        //             array( "DSH", "DASH" ),
        //             array( "IOT", "IOTA" ),
        //             array( "LES", "LEO-EOS" ),
        //             array( "LET", "LEO-ERC20" ),
        //             array( "STJ", "STORJ" ),
        //             array( "TSD", "TUSD" ),
        //             array( "UDC", "USDC" ),
        //             array( "USK", "USDK" ),
        //             array( "UST", "USDt" ),
        //             array( "USTF0", "USDt0" ),
        //             array( "XCH", "XCHF" ),
        //             array( "YYW", "YOYOW" ),
        //             // ...
        //         ),
        //         // $label
        //         // verbose friendly names, BNT > Bancor
        //         array(
        //             array( "BAB", "Bitcoin Cash" ),
        //             array( "BCH", "Bitcoin Cash" ),
        //             array( "LEO", "Unus Sed LEO" ),
        //             array( "LES", "Unus Sed LEO (EOS)" ),
        //             array( "LET", "Unus Sed LEO (ERC20)" ),
        //             // ...
        //         ),
        //         // unit
        //         // maps symbols to unit of measure where applicable
        //         array(
        //             array( "IOT", "Mi|MegaIOTA" ),
        //         ),
        //         // $undl
        //         // maps derivatives symbols to their underlying currency
        //         array(
        //             array( "USTF0", "UST" ),
        //             array( "BTCF0", "BTC" ),
        //             array( "ETHF0", "ETH" ),
        //         ),
        //         // $pool
        //         // maps symbols to underlying network/protocol they operate on
        //         array(
        //             array( 'SAN', 'ETH' ), array( 'OMG', 'ETH' ), array( 'AVT', 'ETH' ), array( "EDO", "ETH" ),
        //             array( 'ESS', 'ETH' ), array( 'ATD', 'EOS' ), array( 'ADD', 'EOS' ), array( "MTO", "EOS" ),
        //             array( 'PNK', 'ETH' ), array( 'BAB', 'BCH' ), array( 'WLO', 'XLM' ), array( "VLD", "ETH" ),
        //             array( 'BTT', 'TRX' ), array( 'IMP', 'ETH' ), array( 'SCR', 'ETH' ), array( "GNO", "ETH" ),
        //             // ...
        //         ),
        //         // explorer
        //         // maps symbols to their recognised block explorer URLs
        //         array(
        //             array(
        //                 "AIO",
        //                 array(
        //                     "https://mainnet.aion.network",
        //                     "https://mainnet.aion.network/#/account/VAL",
        //                     "https://mainnet.aion.network/#/transaction/VAL"
        //                 )
        //             ),
        //             // ...
        //         ),
        //         // $fee
        //         // maps currencies to their withdrawal $fees
        //         [
        //             ["AAA",[0,0]],
        //             ["ABS",[0,131.3]],
        //             ["ADA",[0,0.3]],
        //         ],
        //     ]
        //
        $indexed = array(
            'sym' => $this->index_by($this->safe_value($response, 1, array()), 0),
            'label' => $this->index_by($this->safe_value($response, 2, array()), 0),
            'unit' => $this->index_by($this->safe_value($response, 3, array()), 0),
            'undl' => $this->index_by($this->safe_value($response, 4, array()), 0),
            'pool' => $this->index_by($this->safe_value($response, 5, array()), 0),
            'explorer' => $this->index_by($this->safe_value($response, 6, array()), 0),
            'fees' => $this->index_by($this->safe_value($response, 7, array()), 0),
        );
        $ids = $this->safe_value($response, 0, array());
        $result = array();
        for ($i = 0; $i < count($ids); $i++) {
            $id = $ids[$i];
            if (mb_strpos($id, 'F0') !== false) {
                // we get a lot of F0 currencies, skip those
                continue;
            }
            $code = $this->safe_currency_code($id);
            $label = $this->safe_value($indexed['label'], $id, array());
            $name = $this->safe_string($label, 1);
            $pool = $this->safe_value($indexed['pool'], $id, array());
            $rawType = $this->safe_string($pool, 1);
            $type = ($rawType === null) ? 'other' : 'crypto';
            $feeValues = $this->safe_value($indexed['fees'], $id, array());
            $fees = $this->safe_value($feeValues, 1, array());
            $fee = $this->safe_number($fees, 1);
            $undl = $this->safe_value($indexed['undl'], $id, array());
            $precision = '8'; // default $precision, todo => fix "magic constants"
            $fid = 'f' . $id;
            $result[$code] = array(
                'id' => $fid,
                'uppercaseId' => $id,
                'code' => $code,
                'info' => array( $id, $label, $pool, $feeValues, $undl ),
                'type' => $type,
                'name' => $name,
                'active' => true,
                'deposit' => null,
                'withdraw' => null,
                'fee' => $fee,
                'precision' => intval($precision),
                'limits' => array(
                    'amount' => array(
                        'min' => $this->parse_number($this->parse_precision($precision)),
                        'max' => null,
                    ),
                    'withdraw' => array(
                        'min' => $fee,
                        'max' => null,
                    ),
                ),
                'networks' => array(),
            );
            $networks = array();
            $currencyNetworks = $this->safe_value($response, 8, array());
            $cleanId = str_replace('F0', '', $id);
            for ($j = 0; $j < count($currencyNetworks); $j++) {
                $pair = $currencyNetworks[$j];
                $networkId = $this->safe_string($pair, 0);
                $currencyId = $this->safe_string($this->safe_value($pair, 1, array()), 0);
                if ($currencyId === $cleanId) {
                    $network = $this->safe_network($networkId);
                    $networks[$network] = array(
                        'info' => $networkId,
                        'id' => strtolower($networkId),
                        'network' => $networkId,
                        'active' => null,
                        'deposit' => null,
                        'withdraw' => null,
                        'fee' => null,
                        'precision' => null,
                        'limits' => array(
                            'withdraw' => array(
                                'min' => null,
                                'max' => null,
                            ),
                        ),
                    );
                }
            }
            $keysNetworks = is_array($networks) ? array_keys($networks) : array();
            $networksLength = count($keysNetworks);
            if ($networksLength > 0) {
                $result[$code]['networks'] = $networks;
            }
        }
        return $result;
    }

    public function safe_network($networkId) {
        $networksById = array(
            'BITCOIN' => 'BTC',
            'LITECOIN' => 'LTC',
            'ETHEREUM' => 'ERC20',
            'TETHERUSE' => 'ERC20',
            'TETHERUSO' => 'OMNI',
            'TETHERUSL' => 'LIQUID',
            'TETHERUSX' => 'TRC20',
            'TETHERUSS' => 'EOS',
            'TETHERUSDTAVAX' => 'AVAX',
            'TETHERUSDTSOL' => 'SOL',
            'TETHERUSDTALG' => 'ALGO',
            'TETHERUSDTBCH' => 'BCH',
            'TETHERUSDTKSM' => 'KSM',
            'TETHERUSDTDVF' => 'DVF',
            'TETHERUSDTOMG' => 'OMG',
        );
        return $this->safe_string($networksById, $networkId, $networkId);
