        //          null,
        //          null,
        //          null,
        //          0.0005,
        //          0.0025
        //       )
        //   )
        //
        $rates = array();
        for ($i = 0; $i < count($response); $i++) {
            $fr = $response[$i];
            $rate = $this->parse_funding_rate_history($fr, $market);
            $rates[] = $rate;
        }
        $reversedArray = array();
        $rawRates = $this->filter_by_symbol_since_limit($rates, $symbol, $since, $limit);
