    public function create_deposit_address($code, $params = array ()) {
        /**
         * create a currency deposit address
         * @param {string} $code unified currency $code of the currency for the deposit address
         * @param {array} $params extra parameters specific to the bitfinex2 api endpoint
         * @return {array} an {@link https://docs.ccxt.com/en/latest/manual.html#address-structure address structure}
         */
        $this->load_markets();
        $request = array(
            'op_renew' => 1,
        );
        return $this->fetch_deposit_address($code, array_merge($request, $params));
    }

    public function fetch_deposit_address($code, $params = array ()) {
        /**
         * fetch the deposit $address for a $currency associated with this account
         * @param {string} $code unified $currency $code
         * @param {array} $params extra parameters specific to the bitfinex2 api endpoint
         * @return {array} an {@link https://docs.ccxt.com/en/latest/manual.html#$address-structure $address structure}
         */
        $this->load_markets();
        $currency = $this->currency($code);
        // if not provided explicitly we will try to match using the $currency name
        $network = $this->safe_string($params, 'network', $code);
        $currencyNetworks = $this->safe_value($currency, 'networks', array());
        $currencyNetwork = $this->safe_value($currencyNetworks, $network);
        $networkId = $this->safe_string($currencyNetwork, 'id');
        if ($networkId === null) {
            throw new ArgumentsRequired($this->id . " fetchDepositAddress() could not find a $network for '" . $code . "'. You can specify it by providing the 'network' value inside $params");
        }
        $wallet = $this->safe_string($params, 'wallet', 'exchange');  // 'exchange', 'margin', 'funding' and also old labels 'exchange', 'trading', 'deposit', respectively
        $params = $this->omit($params, 'network', 'wallet');
        $request = array(
            'method' => $networkId,
            'wallet' => $wallet,
            'op_renew' => 0, // a value of 1 will generate a new $address
        );
        $response = $this->privatePostAuthWDepositAddress (array_merge($request, $params));
        //
        //     array(
        //         1582269616687, // MTS Millisecond Time Stamp of the update
        //         'acc_dep', // TYPE Purpose of notification 'acc_dep' for account deposit
        //         null, // MESSAGE_ID unique ID of the message
        //         null, // not documented
        //         array(
        //             null, // PLACEHOLDER
        //             'BITCOIN', // METHOD Method of deposit
        //             'BTC', // CURRENCY_CODE Currency $code of new $address
        //             null, // PLACEHOLDER
        //             '1BC9PZqpUmjyEB54uggn8TFKj49zSDYzqG', // ADDRESS
        //             null, // POOL_ADDRESS
        //         ),
        //         null, // CODE null or integer work in progress
        //         'SUCCESS', // STATUS Status of the notification, SUCCESS, ERROR, FAILURE
        //         'success', // TEXT Text of the notification
        //     )
        //
        $result = $this->safe_value($response, 4, array());
        $poolAddress = $this->safe_string($result, 5);
        $address = ($poolAddress === null) ? $this->safe_string($result, 4) : $poolAddress;
        $tag = ($poolAddress === null) ? null : $this->safe_string($result, 4);
        $this->check_address($address);
        return array(
            'currency' => $code,
            'address' => $address,
            'tag' => $tag,
            'network' => null,
            'info' => $response,
        );
    }

    public function parse_transaction_status($status) {
        $statuses = array(
            'SUCCESS' => 'ok',
            'ERROR' => 'failed',
            'FAILURE' => 'failed',
            'CANCELED' => 'canceled',
            'COMPLETED' => 'ok',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_transaction($transaction, $currency = null) {
        //
        // withdraw
        //
        //     array(
        //         1582271520931, // MTS Millisecond Time Stamp of the update
        //         "acc_wd-req", // TYPE Purpose of notification 'acc_wd-req' account withdrawal request
        //         null, // MESSAGE_ID unique ID of the message
        //         null, // not documented
        //         array(
        //             0, // WITHDRAWAL_ID Unique Withdrawal ID
        //             null, // PLACEHOLDER
        //             "bitcoin", // METHOD Method of withdrawal
        //             null, // PAYMENT_ID Payment ID if relevant
        //             "exchange", // WALLET Sending wallet
        //             1, // AMOUNT Amount of Withdrawal less fee
        //             null, // PLACEHOLDER
        //             null, // PLACEHOLDER
        //             0.0004, // WITHDRAWAL_FEE Fee on withdrawal
        //         ),
        //         null, // CODE null or integer Work in progress
        //         "SUCCESS", // STATUS Status of the notification, it may vary over time SUCCESS, ERROR, FAILURE
        //         "Invalid bitcoin address (abcdef)", // TEXT Text of the notification
        //     )
        //
        // fetchTransactions
        //
        //     array(
        //         13293039, // ID
        //         'ETH', // CURRENCY
        //         'ETHEREUM', // CURRENCY_NAME
        //         null,
        //         null,
        //         1574175052000, // MTS_STARTED
        //         1574181326000, // MTS_UPDATED
        //         null,
        //         null,
        //         'CANCELED', // STATUS
        //         null,
        //         null,
        //         -0.24, // AMOUNT, negative for withdrawals
        //         -0.00135, // FEES
        //         null,
        //         null,
        //         '0x38110e0Fc932CB2BE...........', // DESTINATION_ADDRESS
        //         null,
        //         null,
        //         null,
        //         '0x523ec8945500.....................................', // TRANSACTION_ID
        //         "Purchase of 100 pizzas", // WITHDRAW_TRANSACTION_NOTE, might also be => null
        //     )
        //
        $transactionLength = count($transaction);
        $timestamp = null;
        $updated = null;
        $code = null;
        $amount = null;
        $id = null;
        $status = null;
        $tag = null;
        $type = null;
        $feeCost = null;
        $txid = null;
        $addressTo = null;
        if ($transactionLength === 8) {
            $data = $this->safe_value($transaction, 4, array());
            $timestamp = $this->safe_integer($transaction, 0);
            if ($currency !== null) {
                $code = $currency['code'];
            }
            $feeCost = $this->safe_string($data, 8);
            if ($feeCost !== null) {
                $feeCost = Precise::string_neg($feeCost);
            }
            $amount = $this->safe_number($data, 5);
            $id = $this->safe_value($data, 0);
            $status = 'ok';
            if ($id === 0) {
                $id = null;
                $status = 'failed';
            }
            $tag = $this->safe_string($data, 3);
            $type = 'withdrawal';
        } elseif ($transactionLength === 22) {
            $id = $this->safe_string($transaction, 0);
            $currencyId = $this->safe_string($transaction, 1);
            $code = $this->safe_currency_code($currencyId, $currency);
            $timestamp = $this->safe_integer($transaction, 5);
            $updated = $this->safe_integer($transaction, 6);
            $status = $this->parse_transaction_status($this->safe_string($transaction, 9));
            $amount = $this->safe_string($transaction, 12);
            if ($amount !== null) {
                if (Precise::string_lt($amount, '0')) {
                    $type = 'withdrawal';
                } else {
                    $type = 'deposit';
                }
            }
            $feeCost = $this->safe_string($transaction, 13);
            if ($feeCost !== null) {
                $feeCost = Precise::string_neg($feeCost);
            }
            $addressTo = $this->safe_string($transaction, 16);
            $txid = $this->safe_string($transaction, 20);
        }
        return array(
            'info' => $transaction,
            'id' => $id,
            'txid' => $txid,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'network' => null,
            'addressFrom' => null,
            'address' => $addressTo, // this is actually the $tag for XRP transfers (the address is missing)
            'addressTo' => $addressTo,
            'tagFrom' => null,
            'tag' => $tag, // refix it properly for the $tag from description
            'tagTo' => $tag,
            'type' => $type,
            'amount' => $this->parse_number($amount),
            'currency' => $code,
            'status' => $status,
            'updated' => $updated,
            'fee' => array(
                'currency' => $code,
                'cost' => $this->parse_number($feeCost),
                'rate' => null,
            ),
        );
    }

    public function fetch_trading_fees($params = array ()) {
        /**
         * fetch the trading fees for multiple markets
         * @param {array} $params extra parameters specific to the bitfinex2 api endpoint
         * @return {array} a dictionary of {@link https://docs.ccxt.com/en/latest/manual.html#$fee-structure $fee structures} indexed by $market symbols
         */
        $this->load_markets();
        $response = $this->privatePostAuthRSummary ($params);
        //
        //      Response Spec:
        //      array(
        //         PLACEHOLDER,
        //         PLACEHOLDER,
        //         PLACEHOLDER,
        //         PLACEHOLDER,
        //         array(
        //            array(
        //             MAKER_FEE,
        //             MAKER_FEE,
        //             MAKER_FEE,
        //             PLACEHOLDER,
        //             PLACEHOLDER,
        //             DERIV_REBATE
        //            ),
        //            array(
        //             TAKER_FEE_TO_CRYPTO,
        //             TAKER_FEE_TO_STABLE,
        //             TAKER_FEE_TO_FIAT,
        //             PLACEHOLDER,
        //             PLACEHOLDER,
        //             DERIV_TAKER_FEE
        //            )
        //         ),
        //         PLACEHOLDER,
        //         PLACEHOLDER,
        //         PLACEHOLDER,
        //         PLACEHOLDER,
        //         {
        //             LEO_LEV,
        //             LEO_AMOUNT_AVG
        //         }
        //     )
        //
        //      Example $response:
        //
        //     array(
        //         null,
        //         null,
        //         null,
        //         null,
        //         array(
        //          array( 0.001, 0.001, 0.001, null, null, 0.0002 ),
        //          array( 0.002, 0.002, 0.002, null, null, 0.00065 )
        //         ),
        //         array(
        //          array(
        //              {
        //              curr => 'Total (USD)',
        //              vol => '0',
        //              vol_safe => '0',
        //              vol_maker => '0',
        //              vol_BFX => '0',
        //              vol_BFX_safe => '0',
        //              vol_BFX_maker => '0'
        //              }
        //          ),
        //          array(),
        //          0
        //         ),
        //         array( null, array(), 0 ),
        //         null,
        //         null,
        //         array( leo_lev => '0', leo_amount_avg => '0' )
        //     )
        //
        $result = array();
        $fiat = $this->safe_value($this->options, 'fiat', array());
        $feeData = $this->safe_value($response, 4, array());
        $makerData = $this->safe_value($feeData, 0, array());
        $takerData = $this->safe_value($feeData, 1, array());
        $makerFee = $this->safe_number($makerData, 0);
        $makerFeeFiat = $this->safe_number($makerData, 2);
        $makerFeeDeriv = $this->safe_number($makerData, 5);
        $takerFee = $this->safe_number($takerData, 0);
        $takerFeeFiat = $this->safe_number($takerData, 2);
        $takerFeeDeriv = $this->safe_number($takerData, 5);
        for ($i = 0; $i < count($this->symbols); $i++) {
            $symbol = $this->symbols[$i];
            $market = $this->market($symbol);
            $fee = array(
                'info' => $response,
                'symbol' => $symbol,
                'percentage' => true,
                'tierBased' => true,
            );
            if (is_array($fiat) && array_key_exists($market['quote'], $fiat)) {
                $fee['maker'] = $makerFeeFiat;
                $fee['taker'] = $takerFeeFiat;
            } elseif ($market['contract']) {
                $fee['maker'] = $makerFeeDeriv;
                $fee['taker'] = $takerFeeDeriv;
            } else { // TODO check if stable coin
                $fee['maker'] = $makerFee;
                $fee['taker'] = $takerFee;
            }
            $result[$symbol] = $fee;
        }
        return $result;
    }

    public function fetch_transactions($code = null, $since = null, $limit = null, $params = array ()) {
        /**
         * fetch history of deposits and withdrawals
         * @param {string|null} $code unified $currency $code for the $currency of the transactions, default is null
         * @param {int|null} $since timestamp in ms of the earliest transaction, default is null
         * @param {int|null} $limit max number of transactions to return, default is null
         * @param {array} $params extra parameters specific to the bitfinex2 api endpoint
         * @return {array} a list of {@link https://docs.ccxt.com/en/latest/manual.html#transaction-structure transaction structure}
         */
        $this->load_markets();
        $currency = null;
        $request = array();
        $method = 'privatePostAuthRMovementsHist';
        if ($code !== null) {
            $currency = $this->currency($code);
            $request['currency'] = $currency['uppercaseId'];
            $method = 'privatePostAuthRMovementsCurrencyHist';
        }
        if ($since !== null) {
            $request['start'] = $since;
        }
        if ($limit !== null) {
            $request['limit'] = $limit; // max 1000
        }
        $response = $this->$method (array_merge($request, $params));
        //
        //     array(
        //         array(
        //             13293039, // ID
        //             'ETH', // CURRENCY
        //             'ETHEREUM', // CURRENCY_NAME
        //             null,
        //             null,
        //             1574175052000, // MTS_STARTED
        //             1574181326000, // MTS_UPDATED
        //             null,
        //             null,
        //             'CANCELED', // STATUS
        //             null,
        //             null,
        //             -0.24, // AMOUNT, negative for withdrawals
        //             -0.00135, // FEES
        //             null,
        //             null,
        //             '0x38110e0Fc932CB2BE...........', // DESTINATION_ADDRESS
        //             null,
        //             null,
        //             null,
        //             '0x523ec8945500.....................................', // TRANSACTION_ID
        //             "Purchase of 100 pizzas", // WITHDRAW_TRANSACTION_NOTE, might also be => null
        //         )
        //     )
        //
        return $this->parse_transactions($response, $currency, $since, $limit);
    }

    public function withdraw($code, $amount, $address, $tag = null, $params = array ()) {
        /**
         * make a withdrawal
         * @param {string} $code unified $currency $code
         * @param {float} $amount the $amount to withdraw
         * @param {string} $address the $address to withdraw to
         * @param {string|null} $tag
         * @param {array} $params extra parameters specific to the bitfinex2 api endpoint
         * @return {array} a {@link https://docs.ccxt.com/en/latest/manual.html#$transaction-structure $transaction structure}
         */
        $this->check_address($address);
        $this->load_markets();
        $currency = $this->currency($code);
        // if not provided explicitly we will try to match using the $currency name
        $network = $this->safe_string($params, 'network', $code);
        $params = $this->omit($params, 'network');
        $currencyNetworks = $this->safe_value($currency, 'networks', array());
        $currencyNetwork = $this->safe_value($currencyNetworks, $network);
        $networkId = $this->safe_string($currencyNetwork, 'id');
        if ($networkId === null) {
            throw new ArgumentsRequired($this->id . " fetchDepositAddress() could not find a $network for '" . $code . "'. You can specify it by providing the 'network' value inside $params");
        }
        $wallet = $this->safe_string($params, 'wallet', 'exchange');  // 'exchange', 'margin', 'funding' and also old labels 'exchange', 'trading', 'deposit', respectively
        $params = $this->omit($params, 'network', 'wallet');
        $request = array(
            'method' => $networkId,
            'wallet' => $wallet,
            'amount' => $this->number_to_string($amount),
            'address' => $address,
        );
        if ($tag !== null) {
            $request['payment_id'] = $tag;
        }
        $response = $this->privatePostAuthWWithdraw (array_merge($request, $params));
        //
        //     array(
        //         1582271520931, // MTS Millisecond Time Stamp of the update
        //         "acc_wd-req", // TYPE Purpose of notification 'acc_wd-req' account withdrawal $request
        //         null, // MESSAGE_ID unique ID of the $message
        //         null, // not documented
        //         array(
        //             0, // WITHDRAWAL_ID Unique Withdrawal ID
        //             null, // PLACEHOLDER
        //             "bitcoin", // METHOD Method of withdrawal
        //             null, // PAYMENT_ID Payment ID if relevant
        //             "exchange", // WALLET Sending $wallet
        //             1, // AMOUNT Amount of Withdrawal less fee
        //             null, // PLACEHOLDER
        //             null, // PLACEHOLDER
        //             0.0004, // WITHDRAWAL_FEE Fee on withdrawal
        //         ),
        //         null, // CODE null or integer Work in progress
        //         "SUCCESS", // STATUS Status of the notification, it may vary over time SUCCESS, ERROR, FAILURE
        //         "Invalid bitcoin $address (abcdef)", // TEXT Text of the notification
        //     )
        //
        // in case of failure:
        //
        //     array(
        //         "error",
        //         10001,
        //         "Momentary balance check. Please wait few seconds and try the transfer again."
        //     )
        //
        $statusMessage = $this->safe_string($response, 0);
        if ($statusMessage === 'error') {
            $feedback = $this->id . ' ' . $response;
            $message = $this->safe_string($response, 2, '');
            // same $message as in v1
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $message, $feedback);
            $this->throw_broadly_matched_exception($this->exceptions['broad'], $message, $feedback);
            throw new ExchangeError($feedback); // unknown $message
        }
        $text = $this->safe_string($response, 7);
        if ($text !== 'success') {
            $this->throw_broadly_matched_exception($this->exceptions['broad'], $text, $text);
        }
        $transaction = $this->parse_transaction($response, $currency);
        return array_merge($transaction, array(
            'address' => $address,
        ));
    }

