    }

    public function parse_market($market) {
        //
        // $spot
        //
        //     {
        //         "base_currency":"btc",
        //         "quote_currency":"usdt",
        //         "$symbol":"btc_usdt",
        //         "tick_size":"2",
        //         "size_increment":"4",
        //         "$status":"1",
        //         "base_asset_precision":"8"
        //     }
        //
        //
        // $swap
        //
        //     {
        //         "$symbol":"btcusd",
        //         "underlying_index":"BTC",
        //         "quote_currency":"USD",
        //         "coin":"BTC",
        //         "contract_val":"1",
        //         "listing":null,
        //         "delivery":["07:00:00","15:00:00","23:00:00"],
        //         "size_increment":"0",
        //         "tick_size":"1",
        //         "forwardContractFlag":false,
        //         "priceEndStep":5
        //     }
        //
        $id = $this->safe_string($market, 'symbol');
        $marketType = 'spot';
        $spot = true;
        $swap = false;
        $baseId = $this->safe_string_2($market, 'base_currency', 'coin');
        $quoteId = $this->safe_string($market, 'quote_currency');
        $contractVal = $this->safe_number($market, 'contract_val');
        if ($contractVal !== null) {
            $marketType = 'swap';
            $spot = false;
            $swap = true;
        }
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        $symbol = strtoupper($id);
        if ($spot) {
            $symbol = $base . '/' . $quote;
        }
        $tickSize = $this->safe_string($market, 'tick_size');
        $sizeIncrement = $this->safe_string($market, 'size_increment');
        $precision = array(
            'amount' => $this->parse_number($this->parse_precision($sizeIncrement)),
            'price' => $this->parse_number($this->parse_precision($tickSize)),
        );
        $minAmount = $this->safe_number_2($market, 'min_size', 'base_min_size');
        $status = $this->safe_string($market, 'status');
        $active = null;
        if ($status !== null) {
            $active = ($status === '1');
        }
        $fees = $this->safe_value_2($this->fees, $marketType, 'trading', array());
        return array_merge($fees, array(
            'id' => $id,
            'symbol' => $symbol,
            'base' => $base,
            'quote' => $quote,
            'baseId' => $baseId,
            'quoteId' => $quoteId,
            'info' => $market,
            'type' => $marketType,
            'spot' => $spot,
            'swap' => $swap,
            'active' => $active,
            'precision' => $precision,
            'limits' => array(
                'amount' => array(
                    'min' => $minAmount,
                    'max' => null,
                ),
                'price' => array(
                    'min' => $precision['price'],
                    'max' => null,
                ),
                'cost' => array(
                    'min' => $precision['price'],
                    'max' => null,
                ),
            ),
        ));
    }

    public function fetch_markets_by_type($type, $params = array ()) {
        if ($type === 'spot') {
            $response = $this->dataGetCommonSymbols ($params);
            //
            //     {
            //         "status":"ok",
            //         "ts":1595526622408,
            //         "$data":array(
            //             array(
            //                 "base_currency":"btc",
            //                 "quote_currency":"usdt",
            //                 "symbol":"btc_usdt",
            //                 "tick_size":"2",
            //                 "size_increment":"4",
            //                 "status":"1",
            //                 "base_asset_precision":"8"
            //             ),
            //         )
            //     }
            //
            $data = $this->safe_value($response, 'data', array());
            return $this->parse_markets($data);
        } else if ($type === 'swap') {
            $response = $this->capiGetMarketContracts ($params);
            //
            //     {
            //         "$data":array(
            //             "contractApis":[
            //                 array(
            //                     "instrument_id":"btcusd",
            //                     "underlying_index":"BTC",
            //                     "quote_currency":"USD",
            //                     "coin":"BTC",
            //                     "contract_val":"1",
            //                     "delivery":["07:00:00","15:00:00","23:00:00"],
            //                     "size_increment":"0",
            //                     "tick_size":"1",
            //                     "forwardContractFlag":false,
            //                     "priceEndStep":"5"
            //                 ),
            //             ]
            //         ),
            //         "status":"ok",
            //         "err_code":"00000"
            //     }
            //
            return $this->parse_markets($response);
        } else {
            throw new NotSupported($this->id . ' fetchMarketsByType does not support market $type ' . $type);
        }
    }

