    public function parse_ticker($ticker, $market = null) {
        //
        // spot
        //
        //     fetchTicker
        //
        //     {
        //         "id":"1595538241113",
        //         "$bid":["0.028474000000","1.139400000000"],
        //         "$ask":["0.028482000000","0.353100000000"],
        //         "amount":"2850.6649",
        //         "count":"818",
        //         "$open":"0.02821",
        //         "close":"0.028474",
        //         "low":"0.02821",
        //         "high":"0.029091",
        //         "vol":"79.4548693404"
        //     }
        //
        //     fetchTickers
        //
        //     {
        //         "amount":"30086.8095",
        //         "count":"22450",
        //         "$open":"9525.11",
        //         "close":"9591.81",
        //         "low":"9510.68",
        //         "high":"9659.7",
        //         "vol":"286239092.250461",
        //         "$symbol":"btc_usdt"
        //     }
        //
        // swap
        //
        //     {
        //         "instrument_id":"btcusd",
        //         "$last":"9574.5",
        //         "best_ask":"9575.0",
        //         "best_bid":"9574.0",
        //         "high_24h":"9672",
        //         "low_24h":"9512",
        //         "volume_24h":"567697050",
        //         "$timestamp":"1595538450096"
        //     }
        //
        $timestamp = $this->safe_integer_2($ticker, 'timestamp', 'id');
        $symbol = null;
        $marketId = $this->safe_string_2($ticker, 'instrument_id', 'symbol');
        if (is_array($this->markets_by_id) && array_key_exists($marketId, $this->markets_by_id)) {
            $market = $this->markets_by_id[$marketId];
            $symbol = $market['symbol'];
        } else if ($marketId !== null) {
            $parts = explode('_', $marketId);
            $numParts = is_array($parts) ? count($parts) : 0;
            if ($numParts === 2) {
                list($baseId, $quoteId) = $parts;
                $base = $this->safe_currency_code($baseId);
                $quote = $this->safe_currency_code($quoteId);
                $symbol = $base . '/' . $quote;
            } else {
                $symbol = $marketId;
            }
        }
        if (($symbol === null) && ($market !== null)) {
            $symbol = $market['symbol'];
        }
        $last = $this->safe_number_2($ticker, 'last', 'close');
        $open = $this->safe_number($ticker, 'open');
        $bidVolume = null;
        $askVolume = null;
        $bid = $this->safe_value($ticker, 'bid');
        if ($bid === null) {
            $bid = $this->safe_number($ticker, 'best_bid');
        } else {
            $bidVolume = $this->safe_number($bid, 1);
            $bid = $this->safe_number($bid, 0);
        }
        $ask = $this->safe_value($ticker, 'ask');
        if ($ask === null) {
            $ask = $this->safe_number($ticker, 'best_ask');
        } else {
            $askVolume = $this->safe_number($ask, 1);
            $ask = $this->safe_number($ask, 0);
        }
        $baseVolume = $this->safe_number_2($ticker, 'amount', 'volume_24h');
        $quoteVolume = $this->safe_number($ticker, 'vol');
        $vwap = $this->vwap($baseVolume, $quoteVolume);
        $change = null;
        $percentage = null;
        $average = null;
        if (($last !== null) && ($open !== null)) {
            $change = $last - $open;
            $percentage = $change / $open * 100;
            $average = $this->sum($open, $last) / 2;
        }
        return array(
            'symbol' => $symbol,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'high' => $this->safe_number_2($ticker, 'high', 'high_24h'),
            'low' => $this->safe_number_2($ticker, 'low', 'low_24h'),
            'bid' => $bid,
            'bidVolume' => $bidVolume,
            'ask' => $ask,
            'askVolume' => $askVolume,
            'vwap' => $vwap,
            'open' => $open,
            'close' => $last,
            'last' => $last,
            'previousClose' => null,
            'change' => $change,
            'percentage' => $percentage,
            'average' => $average,
            'baseVolume' => $baseVolume,
            'quoteVolume' => $quoteVolume,
            'info' => $ticker,
        );
    }

