                list($baseId, $quoteId) = $parts;
                $base = $this->safe_currency_code($baseId);
                $quote = $this->safe_currency_code($quoteId);
                $symbol = $base . '/' . $quote;
            } else {
                $symbol = strtoupper($marketId);
            }
        }
        if (($symbol === null) && ($market !== null)) {
            $symbol = $market['symbol'];
            $base = $market['base'];
            $quote = $market['quote'];
        }
        $timestamp = $this->safe_integer($trade, 'created_at');
        $timestamp = $this->safe_integer_2($trade, 'timestamp', 'ts', $timestamp);
        $priceString = $this->safe_string($trade, 'price');
        $amountString = $this->safe_string_2($trade, 'filled_amount', 'order_qty');
        $amountString = $this->safe_string_2($trade, 'size', 'amount', $amountString);
        $price = $this->parse_number($priceString);
        $amount = $this->parse_number($amountString);
        $cost = $this->parse_number(Precise::string_mul($priceString, $amountString));
        $takerOrMaker = $this->safe_string_2($trade, 'exec_type', 'liquidity');
        if ($takerOrMaker === 'M') {
            $takerOrMaker = 'maker';
        } else if ($takerOrMaker === 'T') {
            $takerOrMaker = 'taker';
        }
        $orderType = $this->safe_string($trade, 'type');
        $side = null;
        $type = null;
        if ($orderType !== null) {
            $side = $this->safe_string($trade, 'type');
            $type = $this->parse_order_type($side);
            $side = $this->parse_order_side($side);
        } else {
            $side = $this->safe_string_2($trade, 'side', 'direction');
            $type = $this->parse_order_type($side);
            $side = $this->parse_order_side($side);
        }
        $feeCostString = $this->safe_string($trade, 'fee');
        if ($feeCostString === null) {
            $feeCostString = $this->safe_string($trade, 'filled_fees');
        } else {
            $feeCostString = Precise::string_neg($feeCostString);
        }
        $feeCost = $this->parse_number($feeCostString);
        $fee = null;
        if ($feeCost !== null) {
            $feeCurrency = ($side === 'buy') ? $base : $quote;
            $fee = array(
                // $fee is either a positive number (invitation rebate)
                // or a negative number (transaction $fee deduction)
                // therefore we need to invert the $fee
                // more about it https://github.com/ccxt/ccxt/issues/5909
                'cost' => $feeCost,
                'currency' => $feeCurrency,
            );
        }
        $orderId = $this->safe_string($trade, 'order_id');
        $id = $this->safe_string_2($trade, 'trade_id', 'id');
        return array(
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'id' => $id,
            'order' => $orderId,
            'type' => $type,
            'takerOrMaker' => $takerOrMaker,
            'side' => $side,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => $fee,
        );
    }

    public function fetch_trades($symbol, $limit = null, $since = null, $params = array ()) {
