    public function parse_transaction_status($status) {
        $statuses = array(
            // withdrawals
            'WaitForOperation' => 'pending', // 等待提现
            'OperationLock' => 'pending', // 初审锁定成功
            'OperationSuccess' => 'ok', // 提现成功
            'Cancel' => 'canceled', // 用户撤销
            'Sure' => 'ok', // 复审锁定成功
            'Fail' => 'failed', // 出币异常
            'WaitForChainSure' => 'ok', // 等待链上确认
            // deposits
            'WAIT_0' => 'pending', // 待确认
            'WAIT_1' => 'pending', // 待确认
            'DATA_CHANGE' => 'pending', // 待确认中
            'SUCCESS' => 'ok', // 充值成功
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_transaction($transaction, $currency = null) {
        //
        // fetchDeposits, fetchWithdrawals
        //
        //     {
        //         "$id" => 1171,
        //         "$type" => "withdraw",
        //         "$currency" => "usdt",
        //         "tx_hash" => "ed03094b84eafbe4bc16e7ef766ee959885ee5bcb265872baaa9c64e1cf86c2b",
        //         "$amount" => 7.457467,
        //         "$address" => "rae93V8d2mdoUQHwBDBdM4NHCMehRJAsbm",
        //         "address_tag" => "100040",
        //         "$fee" => 0,
        //         "state" => "safe",
        //         "created_at" => 1510912472199,
        //         "updated_at" => 1511145876575
        //     }
        //
        $id = $this->safe_string($transaction, 'id');
        $address = $this->safe_string($transaction, 'address');
        $tag = $this->safe_string($transaction, 'address_tag');
        $tagFrom = null;
        $tagTo = $tag;
        $addressFrom = null;
        $addressTo = $address;
        $type = $this->safe_string($transaction, 'type');
        if ($type === 'withdraw') {
            $type = 'withdrawal';
        } else if ($type === 'deposit') {
            $type = 'deposit';
        }
        $currencyId = $this->safe_string($transaction, 'currency');
        $code = $this->safe_currency_code($currencyId);
        $amount = $this->safe_number($transaction, 'amount');
        $status = $this->parse_transaction_status($this->safe_string($transaction, 'state'));
        $txid = $this->safe_string($transaction, 'tx_hash');
        $timestamp = $this->safe_integer($transaction, 'created_at');
        $updated = $this->safe_integer($transaction, 'updated_at');
        $feeCost = $this->safe_number($transaction, 'fee');
        $fee = null;
        if ($feeCost !== null) {
            $fee = array(
                'currency' => $code,
                'cost' => $feeCost,
            );
        }
        return array(
            'info' => $transaction,
            'id' => $id,
            'currency' => $code,
            'amount' => $amount,
            'addressFrom' => $addressFrom,
            'addressTo' => $addressTo,
            'address' => $address,
            'tagFrom' => $tagFrom,
            'tagTo' => $tagTo,
            'tag' => $tag,
            'status' => $status,
            'type' => $type,
            'updated' => $updated,
            'txid' => $txid,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'fee' => $fee,
        );
    }

