    public function sign($path, $api = 'public', $method = 'GET', $params = array (), $headers = null, $body = null) {
        $request = '/' . 'api/' . $this->version . '/' . $api . '/' . $path;
        if ($api === 'public') {
            if ($params) {
                $request .= '?' . $this->urlencode($params);
            }
        }
        if ($api === 'private') {
            $this->check_required_credentials();
            if ($method === 'GET') {
                if ($params) {
                    $request .= '?' . $this->urlencode($params);
                }
            }
            $sessionToken = $this->safe_string($this->options, 'accessToken');
            if ($sessionToken === null) {
                throw new AuthenticationError($this->id . ' sign() requires access token');
            }
            $headers = array(
                'Authorization' => 'bearer ' . $sessionToken,
            );
            if ($method === 'POST') {
                $headers['Content-Type'] = 'application/json';
                if ($params) {
                    $rpcPayload = array(
                        'jsonrpc' => '2.0',
                        'id' => $this->nonce(),
                        'method' => '/' . $api . '/' . $path,
                        'params' => $params,
                    );
                    $body = $this->json($rpcPayload);
                }
            }
        }
        $url = $this->urls['api']['rest'] . $request;
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }

    public function handle_errors($code, $reason, $url, $method, $headers, $body, $response, $requestHeaders, $requestBody) {
        if ($response === null) {
            return null; // fallback to the default $error handler
        }
        $error = $this->safe_value($response, 'error');
        if ($error) {
            $feedback = $this->id . ' ' . $body;
            $codeInner = $this->safe_string($error, 'code');
            $message = $this->safe_string($error, 'message');
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $codeInner, $feedback);
            $this->throw_broadly_matched_exception($this->exceptions['broad'], $message, $feedback);
            throw new ExchangeError($feedback); // unknown $message
        }
        return null;
    }
