    public function parse_ledger_entry($item, $currency = null) {
        //
        //     {
        //         "id" => 234467,
        //         "user_id" => 1,
        //         "coin" => "BTC",
        //         "wallet_id" => 27913,
        //         "type" => "Realized P&L",
        //         "amount" => "-0.00000006",
        //         "tx_id" => "",
        //         "address" => "BTCUSD",
        //         "wallet_balance" => "0.03000330",
        //         "exec_time" => "2019-12-09T00:00:25.000Z",
        //         "cross_seq" => 0
        //     }
        //
        //     {
        //         "symbol" => "XRPUSDT",
        //         "side" => "Buy",
        //         "funding" => "",
        //         "orderLinkId" => "linear-order",
        //         "orderId" => "592b7e41-78fd-42e2-9aa3-91e1835ef3e1",
        //         "fee" => "0.00260280",
        //         "change" => "-0.0026028",
        //         "cashFlow" => "0",
        //         "transactionTime" => "1672121182224",
        //         "type" => "TRADE",
        //         "feeRate" => "0.0006",
        //         "size" => "12",
        //         "qty" => "12",
        //         "cashBalance" => "5086.58101322",
        //         "currency" => "USDT",
        //         "category" => "linear",
        //         "tradePrice" => "0.3615",
        //         "tradeId" => "8569c10f-5061-5891-81c4-a54929847eb3"
        //     }
        //
        $currencyId = $this->safe_string_2($item, 'coin', 'currency');
        $code = $this->safe_currency_code($currencyId, $currency);
        $amount = $this->safe_string_2($item, 'amount', 'change');
        $after = $this->safe_string_2($item, 'wallet_balance', 'cashBalance');
        $direction = Precise::string_lt($amount, '0') ? 'out' : 'in';
        $before = null;
        if ($after !== null && $amount !== null) {
            $difference = ($direction === 'out') ? $amount : Precise::string_neg($amount);
            $before = Precise::string_add($after, $difference);
        }
        $timestamp = $this->parse8601($this->safe_string($item, 'exec_time'));
        if ($timestamp === null) {
            $timestamp = $this->safe_integer($item, 'transactionTime');
        }
        $type = $this->parse_ledger_entry_type($this->safe_string($item, 'type'));
        $id = $this->safe_string($item, 'id');
        $referenceId = $this->safe_string($item, 'tx_id');
        return array(
            'id' => $id,
            'currency' => $code,
            'account' => $this->safe_string($item, 'wallet_id'),
            'referenceAccount' => null,
            'referenceId' => $referenceId,
            'status' => null,
            'amount' => $this->parse_number($amount),
            'before' => $this->parse_number($before),
            'after' => $this->parse_number($after),
            'fee' => $this->parse_number($this->safe_string($item, 'fee')),
            'direction' => $direction,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'type' => $type,
            'info' => $item,
        );
    }

    public function parse_ledger_entry_type($type) {
        $types = array(
            'Deposit' => 'transaction',
            'Withdraw' => 'transaction',
            'RealisedPNL' => 'trade',
            'Commission' => 'fee',
            'Refund' => 'cashback',
            'Prize' => 'prize', // ?
            'ExchangeOrderWithdraw' => 'transaction',
            'ExchangeOrderDeposit' => 'transaction',
            // v5
            'TRANSFER_IN' => 'transaction',
            'TRANSFER_OUT' => 'transaction',
            'TRADE' => 'trade',
            'SETTLEMENT' => 'trade',
            'DELIVERY' => 'trade',
            'LIQUIDATION' => 'trade',
            'BONUS' => 'Prize',
            'FEE_REFUND' => 'cashback',
            'INTEREST' => 'transaction',
            'CURRENCY_BUY' => 'trade',
            'CURRENCY_SELL' => 'trade',
        );
        return $this->safe_string($types, $type, $type);
    }

    public function withdraw(string $code, $amount, $address, $tag = null, $params = array ()) {
        /**
         * make a withdrawal
         * @see https://bybit-exchange.github.io/docs/v5/asset/withdraw
         * @param {string} $code unified $currency $code
         * @param {float} $amount the $amount to withdraw
         * @param {string} $address the $address to withdraw to
         * @param {string|null} $tag
         * @param {array} $params extra parameters specific to the bybit api endpoint
         * @return {array} a ~@link https://docs.ccxt.com/#/?id=transaction-structure transaction structure~
         */
        list($tag, $params) = $this->handle_withdraw_tag_and_params($tag, $params);
        $this->load_markets();
        $this->check_address($address);
        $currency = $this->currency($code);
        $request = array(
            'coin' => $currency['id'],
            'amount' => $this->number_to_string($amount),
            'address' => $address,
        );
        if ($tag !== null) {
            $request['tag'] = $tag;
        }
        list($networkCode, $query) = $this->handle_network_code_and_params($params);
        $networkId = $this->network_code_to_id($networkCode);
        if ($networkId !== null) {
            $request['chain'] = strtoupper($networkId);
        }
        $enableUnified = $this->is_unified_enabled();
        $method = ($enableUnified[1]) ? 'privatePostV5AssetWithdrawCreate' : 'privatePostAssetV3PrivateWithdrawCreate';
        $response = $this->$method (array_merge($request, $query));
        //
        //    {
        //         "retCode" => "0",
        //         "retMsg" => "success",
        //         "result" => array(
        //             "id" => "9377266"
        //         ),
        //         "retExtInfo" => array(),
        //         "time" => "1666892894902"
        //     }
        //
        $result = $this->safe_value($response, 'result', array());
        return $this->parse_transaction($result, $currency);
    }

