        $subType = $market['linear'] ? 'linear' : 'inverse';
        $category = $this->safe_string($params, 'category', $subType);
        $request = array(
            'symbol' => $market['id'],
            'intervalTime' => $interval,
            'category' => $category,
        );
        $response = $this->publicGetV5MarketOpenInterest (array_merge($request, $params));
