    public function parse_trade($trade, $market = null) {
        //
        // fetchTrades (public)
        //
        //     {
        //         $id => 43785688,
        //         $symbol => 'BTCUSD',
        //         $price => 7786,
        //         qty => 67,
        //         $side => 'Sell',
        //         time => '2020-03-11T19:18:30.123Z'
        //     }
        //
        // fetchMyTrades, fetchOrderTrades (private)
        //
        //     {
        //         "closed_size" => 0,
        //         "cross_seq" => 277136382,
        //         "exec_fee" => "0.0000001",
        //         "exec_id" => "256e5ef8-abfe-5772-971b-f944e15e0d68",
        //         "exec_price" => "8178.5",
        //         "exec_qty" => 1,
        //         "exec_time" => "1571676941.70682",
        //         "exec_type" => "Trade", //Exec Type Enum
        //         "exec_value" => "0.00012227",
        //         "fee_rate" => "0.00075",
        //         "last_liquidity_ind" => "RemovedLiquidity", //Liquidity Enum
        //         "leaves_qty" => 0,
        //         "nth_fill" => 2,
        //         "order_id" => "7ad50cb1-9ad0-4f74-804b-d82a516e1029",
        //         "order_link_id" => "",
        //         "order_price" => "8178",
        //         "order_qty" => 1,
        //         "order_type" => "Market", //Order Type Enum
        //         "$side" => "Buy", //Side Enum
        //         "$symbol" => "BTCUSD", //Symbol Enum
        //         "user_id" => 1
        //     }
        //
        $id = $this->safe_string_2($trade, 'id', 'exec_id');
        $symbol = null;
        $base = null;
        $marketId = $this->safe_string($trade, 'symbol');
        if (is_array($this->markets_by_id) && array_key_exists($marketId, $this->markets_by_id)) {
            $market = $this->markets_by_id[$marketId];
            $symbol = $market['symbol'];
            $base = $market['base'];
        }
        if (($symbol === null) && ($market !== null)) {
            $symbol = $market['symbol'];
            $base = $market['base'];
        }
        $timestamp = $this->parse8601($this->safe_string($trade, 'time'));
        if ($timestamp === null) {
            $timestamp = $this->safe_timestamp($trade, 'exec_time');
        }
        $side = $this->safe_string_lower($trade, 'side');
        $price = $this->safe_float_2($trade, 'price', 'exec_price');
        $amount = $this->safe_float_2($trade, 'qty', 'exec_qty');
        $cost = $this->safe_float($trade, 'exec_value');
        if ($cost === null) {
            if ($amount !== null) {
                if ($price !== null) {
                    $cost = $amount * $price;
                }
            }
        }
        $lastLiquidityInd = $this->safe_string($trade, 'last_liquidity_ind');
        $takerOrMaker = ($lastLiquidityInd === 'AddedLiquidity') ? 'maker' : 'taker';
        $feeCost = $this->safe_float($trade, 'exec_fee');
        $fee = null;
        if ($feeCost !== null) {
            $fee = array(
                'cost' => $feeCost,
                'currency' => $base,
                'rate' => $this->safe_float($trade, 'fee_rate'),
            );
        }
        return array(
            'id' => $id,
            'info' => $trade,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'symbol' => $symbol,
            'order' => $this->safe_string($trade, 'order_id'),
            'type' => $this->safe_string_lower($trade, 'order_type'),
            'side' => $side,
            'takerOrMaker' => $takerOrMaker,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'fee' => $fee,
        );
    }

