    public function parse_order_status($status) {
        $statuses = array(
            // basic orders
            'Created' => 'open',
            'Rejected' => 'rejected', // order is triggered but failed upon being placed
            'New' => 'open',
            'PartiallyFilled' => 'open',
            'Filled' => 'filled',
            'Cancelled' => 'canceled',
            'PendingCancel' => 'canceling', // the engine has received the cancellation but there is no guarantee that it will be successful
            // conditional orders
            'Active' => 'closed', // order is triggered and placed successfully
            'Untriggered' => 'open', // order waits to be triggered
            'Triggered' => 'closed', // order is triggered
            // 'Cancelled' => 'canceled', // order is cancelled
            // 'Rejected' => 'rejected', // order is triggered but fail to be placed
            'Deactivated' => 'canceled', // conditional order was cancelled before triggering
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function parse_order($order, $market = null) {
        //
        // createOrder
        //
        //     {
        //         "user_id" => 1,
        //         "order_id" => "335fd977-e5a5-4781-b6d0-c772d5bfb95b",
        //         "$symbol" => "BTCUSD",
        //         "$side" => "Buy",
        //         "order_type" => "Limit",
        //         "$price" => 8800,
        //         "qty" => 1,
        //         "time_in_force" => "GoodTillCancel",
        //         "order_status" => "Created",
        //         "last_exec_time" => 0,
        //         "last_exec_price" => 0,
        //         "leaves_qty" => 1,
        //         "cum_exec_qty" => 0,
        //         "cum_exec_value" => 0,
        //         "cum_exec_fee" => 0,
        //         "reject_reason" => "",
        //         "order_link_id" => "",
        //         "created_at" => "2019-11-30T11:03:43.452Z",
        //         "updated_at" => "2019-11-30T11:03:43.455Z"
        //     }
        //
        $timestamp = $this->parse8601($this->safe_string($order, 'created_at'));
        $id = $this->safe_string($order, 'order_id');
        $price = $this->safe_float($order, 'price');
        $average = $this->safe_float($order, 'average_price');
        $amount = $this->safe_float($order, 'qty');
        $filled = $this->safe_float($order, 'cum_exec_qty');
        $remaining = $this->safe_float($order, 'leaves_qty');
        $lastTradeTimestamp = $this->safe_timestamp($order, 'last_exec_time');
        if ($lastTradeTimestamp === 0) {
            $lastTradeTimestamp = null;
        }
        $cost = $this->safe_float($order, 'cum_exec_value');
        if (($filled === null) && ($amount !== null) && ($remaining !== null)) {
            $filled = $amount - $remaining;
        }
        if ($filled !== null) {
            if (($remaining === null) && ($amount !== null)) {
                $remaining = $amount - $filled;
            }
            if ($cost === null) {
                if ($price !== null) {
                    $cost = $price * $filled;
                }
            }
        }
        $status = $this->parse_order_status($this->safe_string($order, 'order_status'));
        $marketId = $this->safe_string($order, 'symbol');
        $symbol = null;
        $base = null;
        if (is_array($this->markets_by_id) && array_key_exists($marketId, $this->markets_by_id)) {
            $market = $this->markets_by_id[$marketId];
            $symbol = $market['symbol'];
            $base = $market['base'];
        }
        if ($market !== null) {
            if ($symbol === null) {
                $symbol = $market['symbol'];
            }
            if ($base === null) {
                $base = $market['base'];
            }
        }
        $side = $this->safe_string_lower($order, 'side');
        $feeCost = $this->safe_float($order, 'cum_exec_fee');
        $fee = null;
        if ($feeCost !== null) {
            $feeCost = abs($feeCost);
            $fee = array(
                'cost' => $feeCost,
                'currency' => $base,
            );
        }
        $type = $this->safe_string_lower($order, 'order_type');
        $clientOrderId = $this->safe_string($order, 'order_link_id');
        if (($clientOrderId !== null) && (strlen($clientOrderId) < 1)) {
            $clientOrderId = null;
        }
        return array(
            'info' => $order,
            'id' => $id,
            'clientOrderId' => $clientOrderId,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'lastTradeTimestamp' => $lastTradeTimestamp,
            'symbol' => $symbol,
            'type' => $type,
            'side' => $side,
            'price' => $price,
            'amount' => $amount,
            'cost' => $cost,
            'average' => $average,
            'filled' => $filled,
            'remaining' => $remaining,
            'status' => $status,
            'fee' => $fee,
            'trades' => null,
        );
    }

