        $clientOrderId = $this->safe_string_2($params, 'order_link_id', 'clientOrderId');
        if ($clientOrderId !== null) {
            $request['order_link_id'] = $clientOrderId;
            $params = $this->omit($params, array( 'order_link_id', 'clientOrderId' ));
