        $stopPx = $this->safe_value_2($params, 'stop_px', 'stopPrice');
        $basePrice = $this->safe_value($params, 'base_price');
        $method = null;
        if ($market['swap']) {
            if ($market['linear']) {
                $method = 'privateLinearPostOrderCreate';
