    public function parse_market(array $market): array {
        //
        //     {
        //         "symbol" => "CLANKER-USDT",
        //         "baseAsset" => "CLANKER",
        //         "marginAsset" => "USDT",
        //         "quoteAsset" => "USDT",
        //         "contractFactor" => "0.01",
        //         "limitMaxQty" => "50000",
        //         "limitMinQty" => "1",
        //         "marketMaxQty" => "10000",
        //         "marketMinQty" => "1",
        //         "pricePrecision" => "8",
        //         "basePrecision" => "8",
        //         "feeRateTaker" => "0.0006",
        //         "feeRateMaker" => "0.0002",
        //         "liqFeeRate" => "0.0006",
        //         "openBuyLimitRateMax" => "0.05",
        //         "openSellLimitRateMax" => "100",
        //         "openBuyLimitRateMin" => "0.98",
        //         "openSellLimitRateMin" => "0.05",
        //         "priceOrderPrecision" => "2",
        //         "baseShowPrecision" => "2",
        //         "maxLeverageLevel" => "20",
        //         "volumePrecision" => "2",
        //         "maxLimitOrderNum" => "200",
        //         "maxPlanOrderNum" => "10",
        //         "reverse" => false,
        //         "onboardTime" => "1763373600000",
        //         "status" => "NORMAL"
        //     }
        //
        $id = $this->safe_string($market, 'symbol');
        $baseId = $this->safe_string($market, 'baseAsset');
        $quoteId = $this->safe_string($market, 'quoteAsset');
        $settleId = $this->safe_string($market, 'marginAsset');
        $base = $this->safe_currency_code($baseId);
        $quote = $this->safe_currency_code($quoteId);
        $settle = $this->safe_currency_code($settleId);
        $symbol = $base . '/' . $quote . ':' . $settle;
        $inverse = $this->safe_bool($market, 'reverse');
        $limitMaxQty = $this->safe_string($market, 'limitMaxQty');
        $marketMaxQty = $this->safe_string($market, 'marketMaxQty');
        $maxAmountString = Precise::string_max($limitMaxQty, $marketMaxQty);
        $marketMinQty = $this->safe_string($market, 'marketMinQty');
        $limitMinQty = $this->safe_string($market, 'limitMinQty');
        $minAmountString = Precise::string_min($marketMinQty, $limitMinQty);
        $contractSize = $this->safe_string($market, 'contractFactor');
        $pricePrecision = $this->parse_precision($this->safe_string($market, 'priceOrderPrecision'));
        $rawAmountPrecision = $this->parse_precision($this->safe_string($market, 'volumePrecision'));
        $amountPrecision = Precise::string_div($rawAmountPrecision, $contractSize);
        $basePrecision = $this->parse_precision($this->safe_string($market, 'basePrecision'));
        $taker = $this->safe_number($market, 'feeRateTaker');
        $maker = $this->safe_number($market, 'feeRateMaker');
        $maxLeverage = $this->safe_number($market, 'maxLeverageLevel');
        $status = $this->safe_string($market, 'status');
        return $this->safe_market_structure(array(
            'id' => $id,
            'symbol' => $symbol,
            'base' => $base,
            'quote' => $quote,
            'settle' => $settle,
            'baseId' => $baseId,
            'quoteId' => $quoteId,
            'settleId' => $settleId,
            'type' => 'swap',
            'spot' => false,
            'margin' => null,
            'swap' => true,
            'future' => false,
            'option' => false,
            'active' => $status === 'NORMAL',
            'contract' => true,
            'linear' => !$inverse,
            'inverse' => $inverse,
            'taker' => $taker,
            'maker' => $maker,
            'contractSize' => $this->parse_number($contractSize),
            'expiry' => null,
            'expiryDatetime' => null,
            'strike' => null,
            'optionType' => null,
            'precision' => array(
                'amount' => $this->parse_number($amountPrecision),
                'price' => $this->parse_number($pricePrecision),
                'base' => $this->parse_number($basePrecision),
            ),
            'limits' => array(
                'leverage' => array(
                    'min' => null,
                    'max' => $maxLeverage,
                ),
                'amount' => array(
                    'min' => $this->parse_number($minAmountString),
                    'max' => $this->parse_number($maxAmountString),
                ),
                'price' => array(
                    'min' => null,
                    'max' => null,
                ),
                'cost' => array(
                    'min' => null,
                    'max' => null,
                ),
            ),
            'created' => $this->parse8601($this->safe_string($market, 'createdAt')),
            'info' => $market,
        ));
    }

