    public function parse_balance($response): array {
        $timestamp = $this->milliseconds();
        $result = array(
            'info' => $response,
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
        );
        for ($i = 0; $i < count($response); $i++) {
            $balance = $response[$i];
            $symbol = $this->safe_string($balance, 'asset');
            $code = $this->safe_currency_code($symbol);
            $account = $this->account();
            $account['total'] = $this->safe_string_2($balance, 'total', 'balance');
            $account['free'] = $this->safe_string_2($balance, 'available', 'availableBalance');
            $result[$code] = $account;
        }
        return $this->safe_balance($result);
    }

    public function transfer(string $code, float $amount, string $fromAccount, string $toAccount, $params = array ()): array {
        /**
         * $transfer $currency internally between wallets on the same account
         *
         * @see https://developers.bydfi.com/en/account#asset-$transfer-between-accounts
         *
         * @param {string} $code unified $currency $code
         * @param {float} $amount amount to $transfer
         * @param {string} $fromAccount 'spot', 'funding', or 'swap'
         * @param {string} $toAccount 'spot', 'funding', or 'swap'
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @return {array} a ~@link https://docs.ccxt.com/?id=$transfer-structure $transfer structure~
         */
        $this->load_markets();
        $currency = $this->currency($code);
        $accountsByType = $this->safe_dict($this->options, 'accountsByType', array());
        $fromId = $this->safe_string($accountsByType, $fromAccount, $fromAccount);
        $toId = $this->safe_string($accountsByType, $toAccount, $toAccount);
        $request = array(
            'asset' => $currency['id'],
            'amount' => $this->currency_to_precision($code, $amount),
            'fromType' => $fromId,
            'toType' => $toId,
        );
        $response = $this->privatePostV1AccountTransfer ($this->extend($request, $params));
        //
        //     {
        //         "code" => 200,
        //         "message" => "success",
        //         "success" => true
        //     }
        //
        $transfer = $this->parse_transfer($response, $currency);
        $transferOptions = $this->safe_dict($this->options, 'transfer', array());
        $fillResponseFromRequest = $this->safe_bool($transferOptions, 'fillResponseFromRequest', true);
        if ($fillResponseFromRequest) {
            $timestamp = $this->milliseconds();
            $transfer['timestamp'] = $timestamp;
            $transfer['datetime'] = $this->iso8601($timestamp);
            $transfer['currency'] = $code;
            $transfer['fromAccount'] = $fromAccount;
            $transfer['toAccount'] = $toAccount;
            $transfer['amount'] = $amount;
        }
        return $transfer;
    }

    public function fetch_transfers(?string $code = null, ?int $since = null, ?int $limit = null, $params = array ()): array {
        /**
         * fetch a history of internal transfers made on an account
         *
         * @see https://developers.bydfi.com/en/account#query-wallet-transfer-records
         *
         * @param {string} $code unified $currency $code of the $currency transferred
         * @param {int} [$since] the earliest time in ms to fetch transfers for
         * @param {int} [$limit] the maximum number of transfers structures to retrieve (default 10)
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @param {int} [$params->until] the latest time in ms to fetch entries for
         * @return {array[]} a list of ~@link https://docs.ccxt.com/?id=transfer-structure transfer structures~
         */
        if ($code === null) {
            throw new ArgumentsRequired($this->id . ' fetchTransfers() requires a $code argument');
        }
        $this->load_markets();
        $currency = $this->currency($code);
        $paginate = $this->safe_bool($params, 'paginate', false);
        if ($paginate) {
            $maxLimit = 50;
            $params = $this->omit($params, 'paginate');
            $params = $this->extend($params, array( 'paginationDirection' => 'backward' ));
            $paginatedResponse = $this->fetch_paginated_call_dynamic('fetchTransfers', $currency['code'], $since, $limit, $params, $maxLimit, true);
            return $this->sort_by($paginatedResponse, 'timestamp');
        }
        $request = array(
            'asset' => $currency['id'],
        );
        $until = null;
        list($until, $params) = $this->handle_option_and_params_2($params, 'fetchTransfers', 'until', 'endTime');
        if ($until === null) {
            $until = $this->milliseconds(); // exchange requires endTime
        }
        if ($since === null) {
            $since = 1; // exchange requires startTime but allows any value
        }
        $request['startTime'] = $since;
        $request['endTime'] = $until;
        if ($limit !== null) {
            $request['rows'] = $limit;
        }
        $response = $this->privateGetV1AccountTransferRecords ($this->extend($request, $params));
        //
        //     {
        //         "code" => 200,
        //         "message" => "success",
        //         "data" => array(
        //             {
        //                 "orderId" => "1209991065294581760",
        //                 "txId" => "6km5fRK83Gwdp43HA479DW1Colh2pKyS",
        //                 "sourceWallet" => "SPOT",
        //                 "targetWallet" => "SWAP",
        //                 "asset" => "USDC",
        //                 "amount" => "100",
        //                 "status" => "SUCCESS",
        //                 "timestamp" => 1766413950000
        //             }
        //         ),
        //         "success" => true
        //     }
        //
        $data = $this->safe_list($response, 'data', array());
        return $this->parse_transfers($data, $currency, $since, $limit);
    }

    public function parse_transfer(array $transfer, ?array $currency = null): array {
        //
        // $transfer
        //     {
        //         "code" => 200,
        //         "message" => "success",
        //         "success" => true
        //     }
        //
        // fetchTransfers
        //     {
        //         "orderId" => "1209991065294581760",
        //         "txId" => "6km5fRK83Gwdp43HA479DW1Colh2pKyS",
        //         "sourceWallet" => "SPOT",
        //         "targetWallet" => "SWAP",
        //         "asset" => "USDC",
        //         "amount" => "100",
        //         "status" => "SUCCESS",
        //         "timestamp" => 1766413950000
        //     }
        //
        $status = $this->safe_string_upper_2($transfer, 'message', 'status');
        $accountsById = $this->safe_dict($this->options, 'accountsById', array());
        $fromId = $this->safe_string_upper($transfer, 'sourceWallet');
        $toId = $this->safe_string_upper($transfer, 'targetWallet');
        $fromAccount = $this->safe_string($accountsById, $fromId, $fromId);
        $toAccount = $this->safe_string($accountsById, $toId, $toId);
        $timestamp = $this->safe_integer($transfer, 'timestamp');
        $currencyId = $this->safe_string($transfer, 'asset');
        return array(
            'info' => $transfer,
            'id' => $this->safe_string($transfer, 'txId'),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'currency' => $this->safe_currency_code($currencyId, $currency),
            'amount' => $this->safe_number($transfer, 'amount'),
            'fromAccount' => $fromAccount,
            'toAccount' => $toAccount,
            'status' => $this->parase_transfer_status($status),
        );
    }

    public function parase_transfer_status(?string $status): ?string {
        $statuses = array(
            'SUCCESS' => 'ok',
            'WAIT' => 'pending',
            'FAILED' => 'failed',
        );
        return $this->safe_string($statuses, $status, $status);
    }

