    public function fetch_transactions_helper($type, $code, $since, $limit, $params) {
        $methodName = ($type === 'deposit') ? 'fetchDeposits' : 'fetchWithdrawals';
        if ($code === null) {
            throw new ArgumentsRequired($this->id . ' ' . $methodName . '() requires a $code argument');
        }
        $this->load_markets();
        $currency = $this->currency($code);
        $paginate = $this->safe_bool($params, 'paginate', false);
        if ($paginate) {
            $maxLimit = 50;
            $params = $this->omit($params, 'paginate');
            $params = $this->extend($params, array( 'paginationDirection' => 'backward' ));
            $paginatedResponse = $this->fetch_paginated_call_dynamic($methodName, $currency['code'], $since, $limit, $params, $maxLimit, true);
            return $this->sort_by($paginatedResponse, 'timestamp');
        }
        $request = array(
            'asset' => $currency['id'],
        );
        $until = null;
        list($until, $params) = $this->handle_option_and_params_2($params, 'fetchTransfers', 'until', 'endTime');
        $now = $this->milliseconds();
        $sevenDays = 7 * 24 * 60 * 60 * 1000; // the maximum range is 7 days
        $startTime = $since;
        if ($startTime === null) {
            if ($until === null) {
                // both $since and $until are null
                $startTime = $now - $sevenDays;
                $until = $now;
            } else {
                // $since is null but $until is defined
                $startTime = $until - $sevenDays;
            }
        } elseif ($until === null) {
            // $until is null but $since is defined
            $delta = $now - $startTime;
            if ($delta > $sevenDays) {
                $until = $startTime . $sevenDays;
            } else {
                $until = $now;
            }
        }
        $request['startTime'] = $startTime;
        $request['endTime'] = $until;
        if ($limit !== null) {
            $request['limit'] = $limit;
        }
        $response = null;
        if ($type === 'deposit') {
            //
            //     {
            //         "code" => 200,
            //         "message" => "success",
            //         "data" => array(
            //             {
            //                 "orderId" => "1208864446987255809",
            //                 "asset" => "USDC",
            //                 "amount" => "200",
            //                 "status" => "SUCCESS",
            //                 "txId" => "0xd059a82a55ffc737722bd23c1ef3db2884ce8525b72ff0b3c038b430ce0c8ca5",
            //                 "network" => "ETH",
            //                 "address" => "0x8346b46f6aa9843c09f79f1c170a37aca83c8fcd",
            //                 "addressTag" => null,
            //                 "finishTime" => 1766145475000,
            //                 "createTime" => 1766145344000
            //             }
            //         ),
            //         "success" => true
            //     }
            //
            $response = $this->privateGetV1SpotDepositRecords ($this->extend($request, $params));
        } else {
            //
            // todo check after withdrawal
            //
            $response = $this->privateGetV1SpotWithdrawRecords ($this->extend($request, $params));
        }
        $data = $this->safe_list($response, 'data', array());
        $transactionParams = array(
            'type' => $type,
        );
        $params = $this->extend($params, $transactionParams);
        return $this->parse_transactions($data, $currency, $since, $limit, $params);
    }

    public function parse_transaction(array $transaction, ?array $currency = null): array {
        //
        // fetchDeposits
        //     {
        //         "orderId" => "1208864446987255809",
        //         "asset" => "USDC",
        //         "amount" => "200",
        //         "status" => "SUCCESS",
        //         "txId" => "0xd059a82a55ffc737722bd23c1ef3db2884ce8525b72ff0b3c038b430ce0c8ca5",
        //         "network" => "ETH",
        //         "address" => "0x8346b46f6aa9843c09f79f1c170a37aca83c8fcd",
        //         "addressTag" => null,
        //         "finishTime" => 1766145475000,
        //         "createTime" => 1766145344000
        //     }
        //
        $currencyId = $this->safe_string($transaction, 'asset');
        $code = $this->safe_currency_code($currencyId, $currency);
        $rawStatus = $this->safe_string_lower($transaction, 'status');
        $timestamp = $this->safe_integer($transaction, 'createTime');
        $fee = null;
        $feeCost = $this->safe_number($transaction, 'fee');
        if ($feeCost !== null) {
            $fee = array(
                'cost' => $feeCost,
                'currency' => null,
            );
        }
        return array(
            'info' => $transaction,
            'id' => $this->safe_string($transaction, 'orderId'),
            'txid' => $this->safe_string($transaction, 'txId'),
            'type' => null,
            'currency' => $code,
            'network' => $this->network_id_to_code($this->safe_string($transaction, 'network')),
            'amount' => $this->safe_number($transaction, 'amount'),
            'status' => $this->parse_transaction_status($rawStatus),
            'timestamp' => $timestamp,
            'datetime' => $this->iso8601($timestamp),
            'address' => $this->safe_string($transaction, 'address'),
            'addressFrom' => null,
            'addressTo' => null,
            'tag' => $this->safe_string($transaction, 'addressTag'),
            'tagFrom' => null,
            'tagTo' => null,
            'updated' => $this->safe_integer($transaction, 'finishTime'),
            'comment' => null,
            'fee' => $fee,
            'internal' => false,
        );
    }

    public function parse_transaction_status(?string $status): ?string {
        $statuses = array(
            'success' => 'ok',
            'wait' => 'pending',
            'failed' => 'failed',
        );
        return $this->safe_string($statuses, $status, $status);
    }

    public function sign($path, mixed $api = 'public', $method = 'GET', $params = array (), mixed $headers = null, mixed $body = null) {
        $url = $this->urls['api'][$api];
        $endpoint = '/' . $path;
        $query = '';
        $sortedParams = $this->keysort($params);
        if ($method === 'GET') {
            $query = $this->urlencode($sortedParams);
            if (strlen($query) !== 0) {
                $endpoint .= '?' . $query;
            }
        }
        if ($api === 'private') {
            $this->check_required_credentials();
            $timestamp = (string) $this->milliseconds();
            if ($method === 'GET') {
                $payload = $this->apiKey . $timestamp . $query;
                $signature = $this->hmac($this->encode($payload), $this->encode($this->secret), 'sha256', 'hex');
                $headers = array(
                    'X-API-KEY' => $this->apiKey,
                    'X-API-TIMESTAMP' => $timestamp,
                    'X-API-SIGNATURE' => $signature,
                );
            } else {
                $body = $this->json($sortedParams);
                $payload = $this->apiKey . $timestamp . $body;
                $signature = $this->hmac($this->encode($payload), $this->encode($this->secret), 'sha256', 'hex');
                $headers = array(
                    'Content-Type' => 'application/json',
                    'X-API-KEY' => $this->apiKey,
                    'X-API-TIMESTAMP' => $timestamp,
                    'X-API-SIGNATURE' => $signature,
                );
            }
        }
        $url .= $endpoint;
        return array( 'url' => $url, 'method' => $method, 'body' => $body, 'headers' => $headers );
    }

    public function handle_errors(int $httpCode, string $reason, string $url, string $method, array $headers, string $body, $response, $requestHeaders, $requestBody) {
        if ($response === null) {
            return null; // fallback to default error handler
        }
        //
        //     {
        //         "code" => 101107,
        //         "message" => "Requires transaction permissions"
        //     }
        //
        $code = $this->safe_string($response, 'code');
        $message = $this->safe_string($response, 'message');
        if ($code !== '200') {
            $feedback = $this->id . ' ' . $body;
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $message, $feedback);
            $this->throw_broadly_matched_exception($this->exceptions['broad'], $message, $feedback);
            $this->throw_exactly_matched_exception($this->exceptions['exact'], $code, $feedback);
            throw new ExchangeError($feedback); // unknown $message
        }
        return null;
    }
}
