        //             array("id":"AED","name":"United Arab Emirates Dirham","min_size":"0.01000000"),
        //             array("id":"AFN","name":"Afghan Afghani","min_size":"0.01000000"),
        //             array("id":"ALL","name":"Albanian Lek","min_size":"0.01000000"),
