        //     }
        //
        // fetchAccountsV3
        //
        //     {
        //         "uuid" => "11111111-1111-1111-1111-111111111111",
        //         "name" => "USDC Wallet",
        //         "currency" => "USDC",
        //         "available_balance" => array(
        //             "value" => "0.0000000000000000",
