    public function fetch_ohlcv(string $symbol, $timeframe = '1m', ?int $since = null, ?int $limit = null, $params = array ()): array {
        /**
         * fetches historical candlestick data containing the open, high, low, and close price, and the volume of a $market
         * @see https://docs.cloud.coinbase.com/advanced-trade-api/reference/retailbrokerageapi_getpubliccandles
         * @param {string} $symbol unified $symbol of the $market to fetch OHLCV data for
         * @param {string} $timeframe the length of time each candle represents
         * @param {int} [$since] timestamp in ms of the earliest candle to fetch
         * @param {int} [$limit] the maximum amount of $candles to fetch, not used by coinbase
         * @param {array} [$params] extra parameters specific to the exchange API endpoint
         * @param {int} [$params->until] the latest time in ms to fetch trades for
         * @param {boolean} [$params->paginate] default false, when true will automatically $paginate by calling this endpoint multiple times. See in the docs all the [availble parameters](https://github.com/ccxt/ccxt/wiki/Manual#pagination-$params)
         * @return {int[][]} A list of $candles ordered, open, high, low, close, volume
         */
        $this->load_markets();
        $maxLimit = 300;
        $limit = ($limit === null) ? $maxLimit : min ($limit, $maxLimit);
        $paginate = false;
        list($paginate, $params) = $this->handle_option_and_params($params, 'fetchOHLCV', 'paginate', false);
        if ($paginate) {
            return $this->fetch_paginated_call_deterministic('fetchOHLCV', $symbol, $since, $limit, $timeframe, $params, $maxLimit - 1);
        }
        $market = $this->market($symbol);
        $request = array(
            'product_id' => $market['id'],
            'granularity' => $this->safe_string($this->timeframes, $timeframe, $timeframe),
        );
        $until = $this->safe_integer_n($params, array( 'until', 'end' ));
        $params = $this->omit($params, array( 'until' ));
        $duration = $this->parse_timeframe($timeframe);
        $requestedDuration = $limit * $duration;
        $sinceString = null;
        if ($since !== null) {
            $sinceString = $this->number_to_string($this->parse_to_int($since / 1000));
        } else {
            $now = (string) $this->seconds();
            $sinceString = Precise::string_sub($now, (string) $requestedDuration);
        }
        $request['start'] = $sinceString;
        if ($until !== null) {
            $request['end'] = $this->number_to_string($this->parse_to_int($until / 1000));
        } else {
            // 300 $candles max
            $request['end'] = Precise::string_add($sinceString, (string) $requestedDuration);
        }
        $response = $this->v3PublicGetBrokerageMarketProductsProductIdCandles (array_merge($request, $params));
        //
        //     {
        //         "candles" => array(
        //             array(
        //                 "start" => "1673391780",
        //                 "low" => "17414.36",
        //                 "high" => "17417.99",
        //                 "open" => "17417.74",
        //                 "close" => "17417.38",
        //                 "volume" => "1.87780853"
        //             ),
        //         )
        //     }
        //
        $candles = $this->safe_list($response, 'candles', array());
        return $this->parse_ohlcvs($candles, $market, $timeframe, $since, $limit);
    }

    public function parse_ohlcv($ohlcv, ?array $market = null): array {
        //
        //     array(
        //         array(
        //             "start" => "1673391780",
        //             "low" => "17414.36",
        //             "high" => "17417.99",
        //             "open" => "17417.74",
        //             "close" => "17417.38",
        //             "volume" => "1.87780853"
        //         ),
        //     )
        //
        return array(
            $this->safe_timestamp($ohlcv, 'start'),
            $this->safe_number($ohlcv, 'open'),
            $this->safe_number($ohlcv, 'high'),
            $this->safe_number($ohlcv, 'low'),
            $this->safe_number($ohlcv, 'close'),
            $this->safe_number($ohlcv, 'volume'),
        );
    }

