                //             }
                //         ),
                //         "message" => "OK"
                //     }
                //
            }
        } else {
            if ($isTriggerOrder) {
                $response = $this->v2PrivatePostFuturesBatchStopOrder ($request);
                //
                //     {
                //         "code" => 0,
                //         "data" => array(
                //             array(
                //                 "code" => 0,
