        );
        $response = $this->v2PrivatePostAssetsRenewalDepositAddress ($this->extend($request, $params));
        //
        //     {
        //         "code" => 0,
        //         "data" => array(
        //             "address" => "0x321bd6479355142334f45653ad5d8b76105a1234",
        //             "memo" => ""
        //         ),
        //         "message" => "OK"
        //     }
        //
        $data = $this->safe_dict($response, 'data', array());
