        //         "pagination" => {
        //             "has_next" => true
        //         }
        //     }
        //
        $data = $this->safe_list($response, 'data', array());
        $modifications = $this->parse_margin_modifications($data, null, 'market', 'swap');
        return $this->filter_by_symbol_since_limit($modifications, $symbol, $since, $limit);
    }
}
