    public function watch_orders($symbol = null, $since = null, $limit = null, $params = array ()) {
        yield $this->load_markets();
        $messageHash = 'order';
        $market = null;
        if ($symbol !== null) {
            $market = $this->market($symbol);
            $symbol = $market['symbol'];
            $messageHash .= ':' . $market['id'];
        } else {
            $messageHash .= ':all';
